import React, { useState } from 'react';
import styled from 'styled-components';
import { businessSectors, businessAreas } from '../data/businessData';

const Registration = () => {
  const [formData, setFormData] = useState({
    businessSector: '',
    businessArea: '',
    location: '',
    employeeCount: '',
    businessAge: '',
    propertyStatus: '',
    monthlyRent: '',
    annualRevenue: '',
    totalCosts: '',
    totalAssets: '',
    totalDebt: '',
    interestPayment: '',
  });
  const [step, setStep] = useState(1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Move to next step or submit data
    if (step === 1 && formData.businessSector) {
      setStep(2);
    } else if (step === 2 && formData.businessArea) {
      console.log('Form submitted:', formData);
      // Here you would typically send the data to your backend
      // and redirect to the next page
    }
  };

  const handleBack = () => {
    setStep(1);
  };

  const renderStepContent = () => {
    switch (step) {
      case 1:
        return (
          <SelectContainer>
            <SelectLabel>Setor Empresarial</SelectLabel>
            <Select
              name="businessSector"
              value={formData.businessSector}
              onChange={handleChange}
              required
            >
              <option value="">-- Selecione o Setor Empresarial --</option>
              {businessSectors.map((sector) => (
                <option key={sector.id} value={sector.id}>
                  {sector.namePortuguese || sector.name}
                </option>
              ))}
            </Select>
          </SelectContainer>
        );
      case 2:
        return (
          <SelectContainer>
            <SelectLabel>Área de Negócio</SelectLabel>
            <Select
              name="businessArea"
              value={formData.businessArea}
              onChange={handleChange}
              required
            >
              <option value="">-- Selecione a Área de Negócio --</option>
              {businessAreas
                .filter(
                  (area) => 
                    area.sectorId === formData.businessSector || 
                    area.sectorId === 'all'
                )
                .map((area) => (
                  <option key={area.id} value={area.id}>
                    {area.namePortuguese || area.name}
                  </option>
                ))}
            </Select>
          </SelectContainer>
        );
      case 3:
        return (
          <>
            <FormTitle>Métricas Estruturais</FormTitle>
            <FormDescription>
              Forneça informações sobre a estrutura da sua empresa para um diagnóstico mais preciso.
            </FormDescription>

            <InputGroup>
              <InputContainer>
                <InputLabel>Localização</InputLabel>
                <Input
                  type="text"
                  name="location"
                  value={formData.location}
                  onChange={handleChange}
                  placeholder="Ex: Lisboa"
                  required
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Número de Funcionários</InputLabel>
                <Input
                  type="number"
                  name="employeeCount"
                  value={formData.employeeCount}
                  onChange={handleChange}
                  placeholder="Ex: 10"
                  required
                  min="1"
                />
              </InputContainer>
            </InputGroup>

            <InputGroup>
              <InputContainer>
                <InputLabel>Idade do Negócio (anos)</InputLabel>
                <Input
                  type="number"
                  name="businessAge"
                  value={formData.businessAge}
                  onChange={handleChange}
                  placeholder="Ex: 5"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Tipo de Propriedade</InputLabel>
                <RadioGroup>
                  <RadioOption>
                    <RadioInput
                      type="radio"
                      name="propertyStatus"
                      value="owned"
                      checked={formData.propertyStatus === 'owned'}
                      onChange={handleChange}
                      required
                    />
                    <RadioLabel>Própria</RadioLabel>
                  </RadioOption>
                  <RadioOption>
                    <RadioInput
                      type="radio"
                      name="propertyStatus"
                      value="rented"
                      checked={formData.propertyStatus === 'rented'}
                      onChange={handleChange}
                      required
                    />
                    <RadioLabel>Arrendada</RadioLabel>
                  </RadioOption>
                </RadioGroup>
              </InputContainer>
            </InputGroup>
          </>
        );
      case 4:
        return (
          <>
            <FormTitle>Métricas Financeiras</FormTitle>
            <FormDescription>
              Estas informações nos ajudarão a calcular indicadores importantes como taxa de esforço e cobertura de juros.
            </FormDescription>

            <InputGroup>
              <InputContainer>
                <InputLabel>Renda Mensal (€)</InputLabel>
                <Input
                  type="number"
                  name="monthlyRent"
                  value={formData.monthlyRent}
                  onChange={handleChange}
                  placeholder="Ex: 2000"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Faturação Anual (€)</InputLabel>
                <Input
                  type="number"
                  name="annualRevenue"
                  value={formData.annualRevenue}
                  onChange={handleChange}
                  placeholder="Ex: 500000"
                  required
                  min="0"
                />
              </InputContainer>
            </InputGroup>

            <InputGroup>
              <InputContainer>
                <InputLabel>Custos Totais Anuais (€)</InputLabel>
                <Input
                  type="number"
                  name="totalCosts"
                  value={formData.totalCosts}
                  onChange={handleChange}
                  placeholder="Ex: 400000"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Total de Ativos (€)</InputLabel>
                <Input
                  type="number"
                  name="totalAssets"
                  value={formData.totalAssets}
                  onChange={handleChange}
                  placeholder="Ex: 1000000"
                  required
                  min="0"
                />
              </InputContainer>
            </InputGroup>

            <InputGroup>
              <InputContainer>
                <InputLabel>Dívida Total (€)</InputLabel>
                <Input
                  type="number"
                  name="totalDebt"
                  value={formData.totalDebt}
                  onChange={handleChange}
                  placeholder="Ex: 200000"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Pagamento Anual de Juros (€)</InputLabel>
                <Input
                  type="number"
                  name="interestPayment"
                  value={formData.interestPayment}
                  onChange={handleChange}
                  placeholder="Ex: 10000"
                  required
                  min="0"
                />
              </InputContainer>
            </InputGroup>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <PageContainer>
      <Header>
        <HeaderContent>
          <Logo>
            <LogoIcon>
              <svg width="32" height="32" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
