import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { businessSectors, businessAreas } from '../data/businessData';

const EditData = () => {
  const [formData, setFormData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState({ text: '', type: '' });
  const [selectedYear, setSelectedYear] = useState(2025);
  const [yearData, setYearData] = useState({
    2023: null,
    2024: null,
    2025: null
  });
  const navigate = useNavigate();

  useEffect(() => {
    // Recuperar dados do localStorage
    const savedData = localStorage.getItem('registrationFormData');

    if (savedData) {
      const data2025 = JSON.parse(savedData);

      // Criar dados para 2024 (25% piores que 2025)
      const data2024 = createHistoricalData(data2025, 0.75);

      // Criar dados para 2023 (40% piores que 2025)
      const data2023 = createHistoricalData(data2025, 0.60);

      // Salvar dados históricos no localStorage se ainda não existirem
      if (!localStorage.getItem('data2024')) {
        localStorage.setItem('data2024', JSON.stringify(data2024));
      } else {
        const saved2024 = JSON.parse(localStorage.getItem('data2024'));
        data2024 = saved2024;
      }

      if (!localStorage.getItem('data2023')) {
        localStorage.setItem('data2023', JSON.stringify(data2023));
      } else {
        const saved2023 = JSON.parse(localStorage.getItem('data2023'));
        data2023 = saved2023;
      }

      setYearData({
        2023: data2023,
        2024: data2024,
        2025: data2025
      });

      setFormData(data2025);
    } else {
      // Redirecionar para o registro se não houver dados
      navigate('/');
    }

    setLoading(false);
  }, [navigate]);

  // Função para criar dados históricos com performance reduzida
  const createHistoricalData = (currentData, factor) => {
    if (!currentData) return null;

    const historicalData = { ...currentData };

    // Ajustar apenas métricas financeiras
    const financialFields = [
      'sales', 'costOfGoodsSold', 'grossProfit', 'operationalCosts',
      'operationalProfit', 'interest', 'taxes', 'netProfit',
      'debt', 'assets', 'customerCount'
    ];

    financialFields.forEach(field => {
      if (historicalData[field] && !isNaN(parseFloat(historicalData[field]))) {
        // Reduzir valores positivos (vendas, lucros, etc.)
        if (field === 'debt') {
          // Para dívida, aumentamos em vez de diminuir (pior performance = mais dívida)
          historicalData[field] = Math.round(parseFloat(historicalData[field]) / factor).toString();
        } else {
          historicalData[field] = Math.round(parseFloat(historicalData[field]) * factor).toString();
        }
      }
    });

    return historicalData;
  };

  const handleYearChange = (year) => {
    setSelectedYear(year);
    setFormData(yearData[year]);
  };

  const handleChange = (e) => {
    // Só permitir edição para o ano 2025
    if (selectedYear !== 2025) return;

    const { name, value } = e.target;
    const updatedFormData = {
      ...formData,
      [name]: value,
    };

    setFormData(updatedFormData);

    // Atualizar também no objeto yearData
    setYearData({
      ...yearData,
      2025: updatedFormData
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Salvar dados atualizados no localStorage
    localStorage.setItem('registrationFormData', JSON.stringify(formData));

    // Mostrar mensagem de sucesso
    setMessage({ 
      text: 'Dados atualizados com sucesso!', 
      type: 'success' 
    });

    // Limpar mensagem após 3 segundos
    setTimeout(() => {
      setMessage({ text: '', type: '' });
    }, 3000);
  };

  if (loading) {
    return <LoadingContainer>A carregar os dados...</LoadingContainer>;
  }

  if (!formData) {
    return <ErrorContainer>Não foram encontrados dados de registo. Por favor, complete o registo primeiro.</ErrorContainer>;
  }

  // Filtrar áreas de negócio com base no setor selecionado
  const filteredAreas = businessAreas.filter(
    area => area.sectorId === formData.businessSector
  );

  return (
    <PageContainer>
      <PageTitle>Editar Dados</PageTitle>

      <YearNavigation>
        <YearButton 
          active={selectedYear === 2023} 
          onClick={() => handleYearChange(2023)}
          disabled={false}
        >
          2023
        </YearButton>
        <YearButton 
          active={selectedYear === 2024} 
          onClick={() => handleYearChange(2024)}
          disabled={false}
        >
          2024
        </YearButton>
        <YearButton 
          active={selectedYear === 2025} 
          onClick={() => handleYearChange(2025)}
          disabled={false}
        >
          2025 (Atual)
        </YearButton>
      </YearNavigation>

      {selectedYear !== 2025 && (
        <ReadOnlyNotice>
          Os dados de anos anteriores são apenas para consulta e não podem ser editados.
        </ReadOnlyNotice>
      )}

      {message.text && (
        <MessageContainer type={message.type}>
          {message.text}
        </MessageContainer>
      )}

      <Form onSubmit={handleSubmit}>
        <SectionCard>
          <SectionTitle>Informações do Negócio</SectionTitle>

          <InputGroup>
            <InputContainer>
              <InputLabel>Setor de Atividade</InputLabel>
              <Select
                name="businessSector"
                value={formData.businessSector}
                onChange={handleChange}
                required
                disabled={selectedYear !== 2025}
              >
                <option value="">Selecione o setor</option>
                {businessSectors.map(sector => (
                  <option key={sector.id} value={sector.id}>
                    {sector.namePortuguese}
                  </option>
                ))}
              </Select>
            </InputContainer>

            <InputContainer>
              <InputLabel>Área de Negócio</InputLabel>
              <Select
                name="businessArea"
                value={formData.businessArea}
                onChange={handleChange}
                required
                disabled={selectedYear !== 2025 || !formData.businessSector}
              >
                <option value="">Selecione a área</option>
                {filteredAreas.map(area => (
                  <option key={area.id} value={area.id}>
                    {area.namePortuguese}
                  </option>
                ))}
              </Select>
            </InputContainer>
          </InputGroup>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Métricas Estruturais</SectionTitle>

          <InputGroup>
            <InputContainer>
              <InputLabel>Localização</InputLabel>
              <Input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                placeholder="Ex: Lisboa"
                required
                disabled={selectedYear !== 2025}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Número de Funcionários</InputLabel>
              <Input
                type="number"
                name="employeeCount"
                value={formData.employeeCount}
                onChange={handleChange}
                placeholder="Ex: 10"
                required
                min="1"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Email do Estabelecimento</InputLabel>
              <Input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Ex: empresa@exemplo.pt"
                required
                disabled={selectedYear !== 2025}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Número de Telefone</InputLabel>
              <Input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Ex: 912345678"
                required
                disabled={selectedYear !== 2025}
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Idade do Negócio (anos)</InputLabel>
              <Input
                type="number"
                name="businessAge"
                value={formData.businessAge}
                onChange={handleChange}
                placeholder="Ex: 5"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Tipo de Propriedade</InputLabel>
              <RadioGroup>
                <RadioOption>
                  <RadioInput
                    type="radio"
                    name="propertyStatus"
                    value="owned"
                    checked={formData.propertyStatus === 'owned'}
                    onChange={handleChange}
                    disabled={selectedYear !== 2025}
                  />
                  <RadioLabel>Própria</RadioLabel>
                </RadioOption>
                <RadioOption>
                  <RadioInput
                    type="radio"
                    name="propertyStatus"
                    value="rented"
                    checked={formData.propertyStatus === 'rented'}
                    onChange={handleChange}
                    disabled={selectedYear !== 2025}
                  />
                  <RadioLabel>Arrendada</RadioLabel>
                </RadioOption>
              </RadioGroup>
            </InputContainer>
          </InputGroup>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Métricas Financeiras</SectionTitle>

          <InputGroup>
            <InputContainer>
              <InputLabel>Vendas (€)</InputLabel>
              <Input
                type="number"
                name="sales"
                value={formData.sales}
                onChange={handleChange}
                placeholder="Ex: 500000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Custo da Mercadoria Vendida (€)</InputLabel>
              <Input
                type="number"
                name="costOfGoodsSold"
                value={formData.costOfGoodsSold}
                onChange={handleChange}
                placeholder="Ex: 300000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Lucro Bruto (€)</InputLabel>
              <Input
                type="number"
                name="grossProfit"
                value={formData.grossProfit}
                onChange={handleChange}
                placeholder="Ex: 200000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Custos Operacionais (€)</InputLabel>
              <Input
                type="number"
                name="operationalCosts"
                value={formData.operationalCosts}
                onChange={handleChange}
                placeholder="Ex: 150000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Lucro Operacional (€)</InputLabel>
              <Input
                type="number"
                name="operationalProfit"
                value={formData.operationalProfit}
                onChange={handleChange}
                placeholder="Ex: 50000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Juros (€)</InputLabel>
              <Input
                type="number"
                name="interest"
                value={formData.interest}
                onChange={handleChange}
                placeholder="Ex: 10000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
