              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Preço do Arrendamento (€/mês)</InputLabel>
              <Input
                type="number"
                name="rentPrice"
                value={formData.rentPrice}
                onChange={handleChange}
                placeholder="Ex: 2000"
                required={formData.propertyStatus === 'rented'}
                min="0"
                disabled={selectedYear !== 2025 || formData.propertyStatus !== 'rented'}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Número de Clientes (anual)</InputLabel>
              <Input
                type="number"
                name="customerCount"
                value={formData.customerCount}
                onChange={handleChange}
                placeholder="Ex: 5000"
                required
                min="0"
                disabled={selectedYear !== 2025}
              />
            </InputContainer>
          </InputGroup>
        </SectionCard>

        <ButtonContainer>
          <ActionButton type="button" onClick={() => navigate('/diagnosis')}>Cancelar</ActionButton>
          <ActionButton 
            type="submit" 
            primary 
            disabled={selectedYear !== 2025}
          >
            Guardar Alterações
          </ActionButton>
        </ButtonContainer>
      </Form>
    </PageContainer>
  );
};

