import React, { useState } from 'react';
import styled from 'styled-components';
import { businessSectors, businessAreas } from '../data/businessData';

const InputGroup = styled.div`
  display: flex;
  gap: 30px;
  margin-bottom: 30px;

  @media (max-width: 768px) {
    flex-direction: column;
    gap: 20px;
  }
`;

const InputContainer = styled.div`
  flex: 1;
`;

const InputLabel = styled.label`
  display: block;
  margin-bottom: 12px;
  font-weight: 500;
  color: #34495e;
  font-size: 20px;
`;

const Input = styled.input`
  width: 100%;
  padding: 18px 20px;
  border: 1px solid #dcdfe6;
  border-radius: 12px;
  font-size: 20px;
  color: #2c3e50;
  background-color: white;
  transition: all 0.3s;

  &:focus {
    outline: none;
    border-color: #3498db;
    box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.2);
  }

  &:hover {
    border-color: #b3c0c8;
  }
`;

const RadioGroup = styled.div`
  display: flex;
  gap: 30px;
  margin-top: 10px;
`;

const RadioOption = styled.div`
  display: flex;
  align-items: center;
`;

const RadioInput = styled.input`
  width: 24px;
  height: 24px;
  margin-right: 10px;
  cursor: pointer;
  accent-color: #3498db;
`;

const RadioLabel = styled.label`
  font-size: 20px;
  color: #2c3e50;
  cursor: pointer;
`;

const Registration = () => {
  const [formData, setFormData] = useState({
    businessSector: '',
    businessArea: '',
    location: '',
    employeeCount: '',
    businessAge: '',
    propertyStatus: '',
    monthlyRent: '',
    annualRevenue: '',
    totalCosts: '',
    totalAssets: '',
    totalDebt: '',
    interestPayment: '',
  });
  const [step, setStep] = useState(1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Move to next step or submit data
    if (step === 1 && formData.businessSector) {
      setStep(2);
    } else if (step === 2 && formData.businessArea) {
      setStep(3);
    } else if (step === 3 && formData.location && formData.employeeCount && formData.businessAge && formData.propertyStatus) {
      // Independentemente da opção selecionada, avançamos para a etapa 4
      setStep(4);
    } else if (step === 4) {
      console.log('Form submitted with all metrics:', formData);
      // Here you would typically send the data to your backend
      // and redirect to the next page
    }
  };

  const handleBack = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };

  const renderStepContent = () => {
    switch (step) {
      case 1:
        return (
          <SelectContainer>
            <SelectLabel>Setor Empresarial</SelectLabel>
            <Select
              name="businessSector"
              value={formData.businessSector}
              onChange={handleChange}
              required
            >
              <option value="">-- Selecione o Setor Empresarial --</option>
              {businessSectors.map((sector) => (
                <option key={sector.id} value={sector.id}>
                  {sector.namePortuguese || sector.name}
                </option>
              ))}
            </Select>
          </SelectContainer>
        );
      case 2:
        return (
          <SelectContainer>
            <SelectLabel>Área de Negócio</SelectLabel>
            <Select
              name="businessArea"
              value={formData.businessArea}
              onChange={handleChange}
              required
            >
              <option value="">-- Selecione a Área de Negócio --</option>
              {businessAreas
                .filter(
                  (area) => 
                    area.sectorId === formData.businessSector || 
                    area.sectorId === 'all'
                )
                .map((area) => (
                  <option key={area.id} value={area.id}>
                    {area.namePortuguese || area.name}
                  </option>
                ))}
            </Select>
          </SelectContainer>
        );
      case 3:
        return (
          <>
            <InputGroup>
              <InputContainer>
                <InputLabel>Localização</InputLabel>
                <Input
                  type="text"
                  name="location"
                  value={formData.location}
                  onChange={handleChange}
                  placeholder="Ex: Lisboa"
                  required
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Número de Funcionários</InputLabel>
                <Input
                  type="number"
                  name="employeeCount"
                  value={formData.employeeCount}
                  onChange={handleChange}
                  placeholder="Ex: 10"
                  required
                  min="1"
                />
              </InputContainer>
            </InputGroup>

            <InputGroup>
              <InputContainer>
                <InputLabel>Idade do Negócio (anos)</InputLabel>
                <Input
                  type="number"
                  name="businessAge"
                  value={formData.businessAge}
                  onChange={handleChange}
                  placeholder="Ex: 5"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Tipo de Propriedade</InputLabel>
                <RadioGroup>
                  <RadioOption>
                    <RadioInput
                      type="radio"
                      name="propertyStatus"
                      value="owned"
                      checked={formData.propertyStatus === 'owned'}
                      onChange={handleChange}
                      required
                    />
                    <RadioLabel>Própria</RadioLabel>
                  </RadioOption>
                  <RadioOption>
                    <RadioInput
                      type="radio"
                      name="propertyStatus"
                      value="rented"
                      checked={formData.propertyStatus === 'rented'}
                      onChange={handleChange}
                      required
                    />
                    <RadioLabel>Arrendada</RadioLabel>
                  </RadioOption>
                </RadioGroup>
              </InputContainer>
            </InputGroup>
          </>
        );
      case 4:
        return (
          <>
            <InputGroup>
              <InputContainer>
                <InputLabel>Renda Mensal (€)</InputLabel>
                <Input
                  type="number"
                  name="monthlyRent"
                  value={formData.monthlyRent}
                  onChange={handleChange}
                  placeholder="Ex: 2000"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Faturação Anual (€)</InputLabel>
                <Input
                  type="number"
                  name="annualRevenue"
                  value={formData.annualRevenue}
                  onChange={handleChange}
                  placeholder="Ex: 500000"
                  required
                  min="0"
                />
              </InputContainer>
            </InputGroup>

            <InputGroup>
              <InputContainer>
                <InputLabel>Custos Totais Anuais (€)</InputLabel>
                <Input
                  type="number"
                  name="totalCosts"
                  value={formData.totalCosts}
                  onChange={handleChange}
                  placeholder="Ex: 400000"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Total de Ativos (€)</InputLabel>
                <Input
                  type="number"
                  name="totalAssets"
                  value={formData.totalAssets}
                  onChange={handleChange}
                  placeholder="Ex: 1000000"
                  required
                  min="0"
                />
              </InputContainer>
            </InputGroup>

            <InputGroup>
              <InputContainer>
                <InputLabel>Dívida Total (€)</InputLabel>
                <Input
                  type="number"
                  name="totalDebt"
                  value={formData.totalDebt}
                  onChange={handleChange}
                  placeholder="Ex: 200000"
                  required
                  min="0"
                />
              </InputContainer>

              <InputContainer>
                <InputLabel>Pagamento Anual de Juros (€)</InputLabel>
                <Input
                  type="number"
                  name="interestPayment"
                  value={formData.interestPayment}
                  onChange={handleChange}
                  placeholder="Ex: 10000"
                  required
                  min="0"
                />
              </InputContainer>
            </InputGroup>
          </>
        );
      default:
        return null;
    }
  };

  return (
    <PageContainer>
      <Header>
        <HeaderContent>
          <Logo>
            <LogoIcon>
              <svg width="32" height="32" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M21 16V8.00002C20.9996 7.6493 20.9071 7.30483 20.7315 7.00119C20.556 6.69754 20.3037 6.44539 20 6.27002L13 2.27002C12.696 2.09449 12.3511 2.00208 12 2.00208C11.6489 2.00208 11.304 2.09449 11 2.27002L4 6.27002C3.69626 6.44539 3.44398 6.69754 3.26846 7.00119C3.09294 7.30483 3.00036 7.6493 3 8.00002V16C3.00036 16.3508 3.09294 16.6952 3.26846 16.9989C3.44398 17.3025 3.69626 17.5547 4 17.73L11 21.73C11.304 21.9056 11.6489 21.998 12 21.998C12.3511 21.998 12.696 21.9056 13 21.73L20 17.73C20.3037 17.5547 20.556 17.3025 20.7315 16.9989C20.9071 16.6952 20.9996 16.3508 21 16Z" stroke="#3498db" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M3.27002 6.96002L12 12L20.73 6.96002" stroke="#3498db" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M12 22.08V12" stroke="#3498db" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </LogoIcon>
            <LogoText>Ferramenta de Diagnóstico Empresarial</LogoText>
          </Logo>
        </HeaderContent>
      </Header>

      <MainContent>
        <FormCard>
          <StepIndicator>
            <StepTitle>Registo</StepTitle>
            <ProgressContainer>
              <ProgressStep active={step >= 1}>
                <StepNumber>1</StepNumber>
                <StepLabel>Setor Empresarial</StepLabel>
              </ProgressStep>
              <ProgressLine active={step >= 2} />
              <ProgressStep active={step >= 2}>
                <StepNumber>2</StepNumber>
                <StepLabel>Área de Negócio</StepLabel>
              </ProgressStep>
              <ProgressLine active={step >= 3} />
              <ProgressStep active={step >= 3}>
                <StepNumber>3</StepNumber>
                <StepLabel>Métricas Estruturais</StepLabel>
              </ProgressStep>
              <ProgressLine active={step >= 4} />
              <ProgressStep active={step >= 4}>
                <StepNumber>4</StepNumber>
                <StepLabel>Métricas Financeiras</StepLabel>
              </ProgressStep>
            </ProgressContainer>
          </StepIndicator>

          <FormTitle>
            {step === 1 ? 'Selecione o Seu Setor Empresarial' : step === 2 ? 'Selecione a Sua Área de Negócio' : step === 3 ? 'Métricas Estruturais' : 'Métricas Financeiras'}
          </FormTitle>

          <FormDescription>
            {step === 1 
              ? 'Escolha o setor que melhor representa as atividades da sua empresa.' 
              : step === 2 
                ? 'Especifique a área dentro do setor selecionado em que a sua empresa opera.' 
                : step === 3 
                  ? 'Forneça informações sobre a estrutura da sua empresa para um diagnóstico mais preciso.' 
                  : 'Estas informações ajudar-nos-ão a calcular indicadores importantes como taxa de esforço e cobertura de juros.'}
          </FormDescription>

          <Form onSubmit={handleSubmit}>
            {renderStepContent()}

            <ButtonContainer>
              {step > 1 && (
                <BackButton type="button" onClick={handleBack}>
                  <BackIcon>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M19 12H5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M12 19L5 12L12 5" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </BackIcon>
                  Voltar
                </BackButton>
              )}
              <NextButton type="submit" fullWidth={step === 1}>
                {step === 4 ? 'Iniciar Diagnóstico' : 'Continuar'}
                {step < 4 && (
                  <NextIcon>
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                      <path d="M5 12H19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                      <path d="M12 5L19 12L12 19" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                    </svg>
                  </NextIcon>
                )}
              </NextButton>
            </ButtonContainer>
          </Form>
        </FormCard>

        <InfoCard>
          <InfoTitle>Porqu Isto É Importante</InfoTitle>
          <InfoText>
            {step === 1 || step === 2 ? 
              'Identificar o seu setor e área de negócio ajuda-nos a adaptar o diagnóstico aos desafios e oportunidades específicos da sua indústria.' :
              step === 3 ? 
              'As métricas estruturais fornecem-nos informações essenciais sobre a dimensão e maturidade do seu negócio, permitindo uma análise mais precisa.' :
              'Os dados financeiros permitem-nos calcular indicadores cruciais como a taxa de esforço e rácio de cobertura de juros, fundamentais para avaliar a saúde financeira da sua empresa.'}
          </InfoText>

          <InfoList>
            {step === 1 || step === 2 ? (
              <>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Avaliação personalizada baseada em referências do setor</InfoItemText>
                </InfoListItem>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Recomendações e melhores práticas específicas do setor</InfoItemText>
                </InfoListItem>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Estratégias de crescimento relevantes para a sua área de negócio</InfoItemText>
                </InfoListItem>
              </>
            ) : step === 3 ? (
              <>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Análise comparativa com empresas de dimensão semelhante</InfoItemText>
                </InfoListItem>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Recomendações adaptadas à fase de desenvolvimento do seu negócio</InfoItemText>
                </InfoListItem>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Avaliação de riscos específicos da sua estrutura empresarial</InfoItemText>
                </InfoListItem>
              </>
            ) : (
              <>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Cálculo de rácios financeiros essenciais para a sua gestão</InfoItemText>
                </InfoListItem>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Identificação de potenciais desequilíbrios na estrutura financeira</InfoItemText>
                </InfoListItem>
                <InfoListItem>
                  <InfoIcon>✓</InfoIcon>
                  <InfoItemText>Recomendações para otimizar a sua performance financeira</InfoItemText>
                </InfoListItem>
              </>
            )}
          </InfoList>
        </InfoCard>
      </MainContent>

      <Footer>
        <FooterContent>
          <FooterText>© 2025 Ferramenta de Diagnóstico Empresarial. Todos os direitos reservados.</FooterText>
        </FooterContent>
      </Footer>
    </PageContainer>
  );
};

