const BusinessMap = () => {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: "YOUR_API_KEY" // Substitua pela sua chave de API do Google Maps
  });

  const [map, setMap] = useState(null);
  const [selectedBusiness, setSelectedBusiness] = useState(null);
  const [businesses, setBusinesses] = useState(mockBusinesses);
  const [filteredBusinesses, setFilteredBusinesses] = useState(mockBusinesses);
  const [filters, setFilters] = useState({
    sector: '',
    area: '',
    parish: '',
    radius: '',
    forSale: false
  });
  const [showPerformance, setShowPerformance] = useState(false);
  const [performanceData, setPerformanceData] = useState(null);

  const onLoad = useCallback(function callback(map) {
    setMap(map);
  }, []);

  const onUnmount = useCallback(function callback() {
    setMap(null);
  }, []);

  // Filtrar negócios com base nos critérios selecionados
  useEffect(() => {
    let filtered = [...businesses];

    if (filters.sector) {
      filtered = filtered.filter(business => business.businessSector === filters.sector);
    }

    if (filters.area) {
      filtered = filtered.filter(business => business.businessArea === filters.area);
    }

    if (filters.parish) {
      filtered = filtered.filter(business => business.location === filters.parish);
    }

    if (filters.forSale) {
      filtered = filtered.filter(business => business.forSale);
    }

    // Filtro por raio seria implementado aqui usando a API de geometria do Google Maps
    // Isso requer cálculos de distância entre pontos geográficos

    setFilteredBusinesses(filtered);

    // Atualizar dados de desempenho com base nos filtros
    if (filters.sector || filters.area) {
      let performanceMetrics = null;

      if (filters.area && performanceAverages.areas[filters.area]) {
        performanceMetrics = performanceAverages.areas[filters.area];
      } else if (filters.sector && performanceAverages.sectors[filters.sector]) {
        performanceMetrics = performanceAverages.sectors[filters.sector];
      }

      setPerformanceData(performanceMetrics);
      setShowPerformance(!!performanceMetrics);
    } else {
      setShowPerformance(false);
    }
  }, [filters, businesses]);

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilters(prev => ({
      ...prev,
      [name]: value
    }));

    // Limpar área de negócio se o setor for alterado
    if (name === 'sector') {
      setFilters(prev => ({
        ...prev,
        area: ''
      }));
    }
  };

  const toggleForSaleFilter = () => {
    setFilters(prev => ({
      ...prev,
      forSale: !prev.forSale
    }));
  };

  const formatCurrency = (value) => {
    return new Intl.NumberFormat('pt-PT', {
      style: 'currency',
      currency: 'EUR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(value);
  };

  const getSaleTypeText = (type) => {
    switch (type) {
      case 'venda': return 'Estabelecimento à venda';
      case 'arrendamento': return 'Estabelecimento para arrendamento';
      case 'trespasse': return 'Trespasse de estabelecimento';
      default: return 'Estabelecimento à venda';
    }
  };

  return (
    <PageContainer>
      <MainContent>
        <PageTitle>Mapa de Comércio em Lisboa</PageTitle>

        <FilterSection>
          <FilterGroup>
            <FilterLabel>Setor de Negócio</FilterLabel>
            <FilterSelect 
              name="sector" 
              value={filters.sector} 
              onChange={handleFilterChange}
            >
              <option value="">Todos os Setores</option>
              {Object.entries(businessSectors).map(([key, value]) => (
                <option key={key} value={key}>{value}</option>
              ))}
            </FilterSelect>
          </FilterGroup>

          <FilterGroup>
            <FilterLabel>Área de Negócio</FilterLabel>
            <FilterSelect 
              name="area" 
              value={filters.area} 
              onChange={handleFilterChange}
              disabled={!filters.sector}
            >
              <option value="">Todas as Áreas</option>
              {filters.sector && businessAreas[filters.sector] ? 
                Object.entries(businessAreas[filters.sector]).map(([key, value]) => (
                  <option key={key} value={key}>{value}</option>
                )) : null
              }
            </FilterSelect>
          </FilterGroup>

          <FilterGroup>
            <FilterLabel>Freguesia</FilterLabel>
            <FilterSelect 
              name="parish" 
              value={filters.parish} 
              onChange={handleFilterChange}
            >
              <option value="">Todas as Freguesias</option>
              {lisboaParishes.map(parish => (
                <option key={parish} value={parish}>{parish}</option>
              ))}
            </FilterSelect>
          </FilterGroup>

          <FilterGroup>
            <FilterLabel>Raio (km)</FilterLabel>
            <FilterSelect 
              name="radius" 
              value={filters.radius} 
              onChange={handleFilterChange}
            >
              <option value="">Sem limite</option>
              <option value="1">1 km</option>
              <option value="2">2 km</option>
              <option value="5">5 km</option>
              <option value="10">10 km</option>
            </FilterSelect>
          </FilterGroup>

          <ForSaleFilter>
            <ForSaleCheckbox 
              type="checkbox" 
              id="forSale" 
              checked={filters.forSale} 
              onChange={toggleForSaleFilter} 
            />
            <ForSaleLabel htmlFor="forSale">Apenas Estabelecimentos à Venda</ForSaleLabel>
          </ForSaleFilter>
        </FilterSection>

        {showPerformance && performanceData && (
          <PerformanceSection>
            <SectionTitle>
              Médias de Desempenho: {filters.area ? businessAreas[filters.sector][filters.area] : businessSectors[filters.sector]}
            </SectionTitle>
            <PerformanceGrid>
              <PerformanceCard>
                <PerformanceLabel>Vendas Médias</PerformanceLabel>
                <PerformanceValue>{formatCurrency(performanceData.sales)}</PerformanceValue>
              </PerformanceCard>

              <PerformanceCard>
                <PerformanceLabel>Lucro Bruto Médio</PerformanceLabel>
                <PerformanceValue>{formatCurrency(performanceData.grossProfit)}</PerformanceValue>
              </PerformanceCard>

              <PerformanceCard>
                <PerformanceLabel>Lucro Operacional Médio</PerformanceLabel>
                <PerformanceValue>{formatCurrency(performanceData.operationalProfit)}</PerformanceValue>
              </PerformanceCard>

              <PerformanceCard>
                <PerformanceLabel>Lucro Líquido Médio</PerformanceLabel>
                <PerformanceValue>{formatCurrency(performanceData.netProfit)}</PerformanceValue>
              </PerformanceCard>

              <PerformanceCard>
                <PerformanceLabel>Margem Bruta Média</PerformanceLabel>
                <PerformanceValue>{performanceData.grossMargin}%</PerformanceValue>
              </PerformanceCard>

              <PerformanceCard>
                <PerformanceLabel>Margem Operacional Média</PerformanceLabel>
                <PerformanceValue>{performanceData.operationalMargin}%</PerformanceValue>
              </PerformanceCard>

              <PerformanceCard>
                <PerformanceLabel>Margem Líquida Média</PerformanceLabel>
                <PerformanceValue>{performanceData.netMargin}%</PerformanceValue>
              </PerformanceCard>
            </PerformanceGrid>
          </PerformanceSection>
        )}

        <MapContainer>
          {isLoaded ? (
            <GoogleMap
              mapContainerStyle={mapContainerStyle}
              center={center}
              zoom={13}
              onLoad={onLoad}
              onUnmount={onUnmount}
            >
              {filteredBusinesses.map(business => (
                <Marker
                  key={business.id}
                  position={business.position}
                  icon={{
                    url: business.forSale 
                      ? 'http://maps.google.com/mapfiles/ms/icons/red-dot.png' 
                      : 'http://maps.google.com/mapfiles/ms/icons/blue-dot.png',
                    scaledSize: new window.google.maps.Size(40, 40)
                  }}
                  onClick={() => setSelectedBusiness(business)}
                />
              ))}

              {selectedBusiness && (
                <InfoWindow
                  position={selectedBusiness.position}
                  onCloseClick={() => setSelectedBusiness(null)}
                >
                  <InfoWindowContent>
                    <BusinessName>{selectedBusiness.name}</BusinessName>

                    {selectedBusiness.forSale && (
                      <ForSaleTag>{getSaleTypeText(selectedBusiness.saleType)}</ForSaleTag>
                    )}

                    <BusinessInfo>
                      <InfoItem>
                        <InfoLabel>Setor:</InfoLabel>
                        <InfoValue>{businessSectors[selectedBusiness.businessSector]}</InfoValue>
                      </InfoItem>

                      <InfoItem>
                        <InfoLabel>Área:</InfoLabel>
                        <InfoValue>
                          {businessAreas[selectedBusiness.businessSector][selectedBusiness.businessArea]}
                        </InfoValue>
                      </InfoItem>

                      <InfoItem>
                        <InfoLabel>Morada:</InfoLabel>
                        <InfoValue>{selectedBusiness.address}</InfoValue>
                      </InfoItem>

                      <InfoItem>
                        <InfoLabel>Freguesia:</InfoLabel>
                        <InfoValue>{selectedBusiness.location}</InfoValue>
                      </InfoItem>

                      <InfoItem>
                        <InfoLabel>Funcionários:</InfoLabel>
                        <InfoValue>{selectedBusiness.employeeCount}</InfoValue>
                      </InfoItem>

                      <InfoItem>
                        <InfoLabel>Telefone:</InfoLabel>
                        <InfoValue>{selectedBusiness.phone}</InfoValue>
                      </InfoItem>

                      <InfoItem>
                        <InfoLabel>Email:</InfoLabel>
                        <InfoValue>{selectedBusiness.email}</InfoValue>
                      </InfoItem>
                    </BusinessInfo>
                  </InfoWindowContent>
                </InfoWindow>
              )}
            </GoogleMap>
          ) : (
            <LoadingContainer>A carregar o mapa...</LoadingContainer>
          )}
        </MapContainer>

        <MapLegend>
          <LegendItem>
            <LegendIcon color="#4285F4" />
            <LegendText>Estabelecimento Ativo</LegendText>
          </LegendItem>
          <LegendItem>
            <LegendIcon color="#EA4335" />
            <LegendText>Estabelecimento à Venda/Arrendamento/Trespasse</LegendText>
          </LegendItem>
        </MapLegend>
      </MainContent>
    </PageContainer>
  );
};

