import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { businessSectors, businessAreas } from '../data/businessData';

// Corrigir o problema dos ícones do Leaflet
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;

// Ícones personalizados mais visíveis
const normalIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-blue.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

const forSaleIcon = new L.Icon({
  iconUrl: 'https://raw.githubusercontent.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// Lista de freguesias de Lisboa
const lisboaParishes = [
  'Ajuda', 'Alcântara', 'Alvalade', 'Areeiro', 'Arroios', 'Avenidas Novas', 
  'Beato', 'Belém', 'Benfica', 'Campo de Ourique', 'Campolide', 'Carnide', 
  'Estrela', 'Lumiar', 'Marvila', 'Misericórdia', 'Olivais', 'Parque das Nações', 
  'Penha de França', 'Santa Clara', 'Santa Maria Maior', 'Santo António', 
  'São Domingos de Benfica', 'São Vicente'
];

// Dados mockados de estabelecimentos em Lisboa com mais informações
const mockBusinesses = [
  {
    id: 1,
    name: "Café Lisboa",
    position: { lat: 38.7173, lng: -9.1378 },
    sector: "food",
    area: "cafe",
    parish: "Santa Maria Maior",
    address: "Praça do Comércio 3, 1100-148 Lisboa",
    phone: "+351 213 456 789",
    rating: 4.2,
    forSale: false
  },
  {
    id: 2,
    name: "Restaurante Tejo",
    position: { lat: 38.7103, lng: -9.1448 },
    sector: "food",
    area: "restaurant",
    parish: "Misericórdia",
    address: "Rua do Alecrim 12, 1200-018 Lisboa",
    phone: "+351 213 789 456",
    rating: 4.5,
    forSale: true,
    saleType: "business",
    price: 120000
  },
  {
    id: 3,
    name: "Loja de Roupas Chiado",
    position: { lat: 38.7113, lng: -9.1408 },
    sector: "retail",
    area: "clothing",
    parish: "Santa Maria Maior",
    address: "Rua Garrett 31, 1200-203 Lisboa",
    phone: "+351 213 123 456",
    rating: 4.0,
    forSale: false
  },
  {
    id: 4,
    name: "Padaria Tradicional",
    position: { lat: 38.7153, lng: -9.1428 },
    sector: "food",
    area: "bakery",
    parish: "Misericórdia",
    address: "Rua da Bica de Duarte Belo 45, 1200-059 Lisboa",
    phone: "+351 213 987 654",
    rating: 4.7,
    forSale: true,
    saleType: "property",
    price: 350000
  },
  {
    id: 5,
    name: "Mercearia do Bairro",
    position: { lat: 38.7193, lng: -9.1398 },
    sector: "retail",
    area: "grocery",
    parish: "Santa Maria Maior",
    address: "Rua dos Bacalhoeiros 18, 1100-070 Lisboa",
    phone: "+351 213 654 321",
    rating: 4.3,
    forSale: false
  },
  {
    id: 6,
    name: "Farmácia Alfama",
    position: { lat: 38.7133, lng: -9.1308 },
    sector: "health",
    area: "pharmacy",
    parish: "Santa Maria Maior",
    address: "Rua de São Miguel 29, 1100-544 Lisboa",
    phone: "+351 218 862 025",
    rating: 4.6,
    forSale: false
  },
  {
    id: 7,
    name: "Livraria Bertrand",
    position: { lat: 38.7118, lng: -9.1422 },
    sector: "retail",
    area: "bookstore",
    parish: "Santa Maria Maior",
    address: "Rua Garrett 73-75, 1200-203 Lisboa",
    phone: "+351 213 476 122",
    rating: 4.8,
    forSale: false
  },
  {
    id: 8,
    name: "Bar Bairro Alto",
    position: { lat: 38.7143, lng: -9.1438 },
    sector: "food",
    area: "bar",
    parish: "Misericórdia",
    address: "Rua do Diário de Notícias 23, 1200-142 Lisboa",
    phone: "+351 213 430 989",
    rating: 4.4,
    forSale: true,
    saleType: "business",
    price: 180000
  },
  {
    id: 9,
    name: "Hotel Baixa",
    position: { lat: 38.7163, lng: -9.1358 },
    sector: "hospitality",
    area: "hotel",
    parish: "Santa Maria Maior",
    address: "Rua da Prata 116, 1100-419 Lisboa",
    phone: "+351 213 461 381",
    rating: 4.5,
    forSale: false
  },
  {
    id: 10,
    name: "Pastelaria Belém",
    position: { lat: 38.6973, lng: -9.2033 },
    sector: "food",
    area: "bakery",
    parish: "Belém",
    address: "Rua de Belém 84-92, 1300-085 Lisboa",
    phone: "+351 213 637 423",
    rating: 4.9,
    forSale: false
  },
  {
    id: 11,
    name: "Loja de Souvenirs",
    position: { lat: 38.7123, lng: -9.1368 },
    sector: "retail",
    area: "gifts",
    parish: "Santa Maria Maior",
    address: "Rua Augusta 102, 1100-053 Lisboa",
    phone: "+351 213 421 876",
    rating: 3.8,
    forSale: true,
    saleType: "business",
    price: 75000
  },
  {
    id: 12,
    name: "Clínica Dental",
    position: { lat: 38.7233, lng: -9.1488 },
    sector: "health",
    area: "dental",
    parish: "Santo António",
    address: "Avenida da Liberdade 129, 1250-140 Lisboa",
    phone: "+351 213 256 789",
    rating: 4.7,
    forSale: false
  }
];

const BusinessMap = () => {
  const [businesses, setBusinesses] = useState(mockBusinesses);
  const [filteredBusinesses, setFilteredBusinesses] = useState(mockBusinesses);
  const [selectedBusiness, setSelectedBusiness] = useState(null);
  const [selectedSector, setSelectedSector] = useState("");
  const [selectedArea, setSelectedArea] = useState("");
  const [selectedParish, setSelectedParish] = useState("");
  const [radius, setRadius] = useState(2);
  const [showOnlyForSale, setShowOnlyForSale] = useState(false);
  const [averageRatings, setAverageRatings] = useState({});

  // Filtrar estabelecimentos
  useEffect(() => {
    let filtered = businesses;

    if (selectedSector) {
      filtered = filtered.filter(business => business.sector === selectedSector);
    }

    if (selectedArea) {
      filtered = filtered.filter(business => business.area === selectedArea);
    }

    if (selectedParish) {
      filtered = filtered.filter(business => business.parish === selectedParish);
    }

    if (showOnlyForSale) {
      filtered = filtered.filter(business => business.forSale);
    }

    setFilteredBusinesses(filtered);
  }, [businesses, selectedSector, selectedArea, selectedParish, radius, showOnlyForSale]);

  // Calcular médias
  useEffect(() => {
    const calculateAverages = () => {
      const sectorRatings = {};
      const areaRatings = {};

      businesses.forEach(business => {
        if (business.sector) {
          if (!sectorRatings[business.sector]) {
            sectorRatings[business.sector] = { sum: 0, count: 0 };
          }
          sectorRatings[business.sector].sum += business.rating;
          sectorRatings[business.sector].count += 1;
        }

        if (business.area) {
          if (!areaRatings[business.area]) {
            areaRatings[business.area] = { sum: 0, count: 0 };
          }
          areaRatings[business.area].sum += business.rating;
          areaRatings[business.area].count += 1;
        }
      });

      const averages = {};

      Object.keys(sectorRatings).forEach(sector => {
        const { sum, count } = sectorRatings[sector];
        averages[`sector_${sector}`] = (sum / count).toFixed(1);
      });

      Object.keys(areaRatings).forEach(area => {
        const { sum, count } = areaRatings[area];
        averages[`area_${area}`] = (sum / count).toFixed(1);
      });

      setAverageRatings(averages);
    };

    calculateAverages();
  }, [businesses]);

  // Funções auxiliares para obter nomes
  const getSectorName = (sectorId) => {
    const sector = businessSectors.find(s => s.id === sectorId);
    return sector ? sector.namePortuguese : sectorId;
  };

  const getAreaName = (areaId) => {
    const area = businessAreas.find(a => a.id === areaId);
    return area ? area.namePortuguese : areaId;
  };

  return (
    <PageContainer>
      <FilterSidebar>
        <SidebarTitle>Filtros</SidebarTitle>

        <FilterGroup>
          <FilterLabel>Setor</FilterLabel>
          <Select 
            value={selectedSector}
            onChange={(e) => {
              setSelectedSector(e.target.value);
              setSelectedArea("");
            }}
          >
            <option value="">Todos os setores</option>
            {Object.keys(businessSectors).map(sector => (
              <option key={sector} value={sector}>
                {businessSectors[sector].namePortuguese}
              </option>
            ))}
          </Select>
        </FilterGroup>

        <FilterGroup>
          <FilterLabel>Área de Negócio</FilterLabel>
          <Select 
            value={selectedArea}
            onChange={(e) => setSelectedArea(e.target.value)}
            disabled={!selectedSector}
          >
            <option value="">Todas as áreas</option>
            {selectedSector && businessSectors[selectedSector].areas.map(areaId => (
              <option key={areaId} value={areaId}>
                {businessAreas[areaId].namePortuguese}
              </option>
            ))}
          </Select>
        </FilterGroup>

        <FilterGroup>
          <FilterLabel>Freguesia</FilterLabel>
          <Select 
            value={selectedParish}
            onChange={(e) => setSelectedParish(e.target.value)}
          >
            <option value="">Todas as freguesias</option>
            {lisboaParishes.map(parish => (
              <option key={parish} value={parish}>{parish}</option>
            ))}
          </Select>
        </FilterGroup>

        <FilterGroup>
          <FilterLabel>Raio (km)</FilterLabel>
          <RangeInput 
            type="range" 
            min="0.5" 
            max="5" 
            step="0.5" 
            value={radius}
            onChange={(e) => setRadius(parseFloat(e.target.value))}
          />
          <RangeValue>{radius} km</RangeValue>
        </FilterGroup>

        <FilterGroup>
          <CheckboxLabel>
            <Checkbox 
              type="checkbox" 
              checked={showOnlyForSale}
              onChange={(e) => setShowOnlyForSale(e.target.checked)}
            />
            Apenas à venda
          </CheckboxLabel>
        </FilterGroup>

        <StatsContainer>
          <StatsTitle>Médias de Desempenho</StatsTitle>

          {selectedSector && averageRatings.sectors && averageRatings.sectors[selectedSector] && (
            <StatItem>
              <StatLabel>{businessSectors[selectedSector].namePortuguese}</StatLabel>
              <StatValue>{averageRatings.sectors[selectedSector].toFixed(1)}</StatValue>
            </StatItem>
          )}

          {selectedArea && averageRatings.areas && averageRatings.areas[selectedArea] && (
            <StatItem>
              <StatLabel>{businessAreas[selectedArea].namePortuguese}</StatLabel>
              <StatValue>{averageRatings.areas[selectedArea].toFixed(1)}</StatValue>
            </StatItem>
          )}

          {(!selectedSector && !selectedArea) && (
            <StatItem>
              <StatLabel>Média Geral</StatLabel>
              <StatValue>{averageRatings.overall ? averageRatings.overall.toFixed(1) : "N/A"}</StatValue>
            </StatItem>
          )}
        </StatsContainer>

        <LegendContainer>
          <LegendTitle>Legenda</LegendTitle>
          <LegendItem>
            <LegendIcon forSale={false} />
            <LegendText>Estabelecimento normal</LegendText>
          </LegendItem>
          <LegendItem>
            <LegendIcon forSale={true} />
            <LegendText>Estabelecimento à venda</LegendText>
          </LegendItem>
        </LegendContainer>
      </FilterSidebar>

      <ContentContainer>
        <MapWrapper>
          <MapContainer 
            center={[38.7223, -9.1393]} 
            zoom={14} 
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            {filteredBusinesses.map(business => (
              <Marker 
                key={business.id}
                position={[business.position.lat, business.position.lng]}
                icon={business.forSale ? forSaleIcon : normalIcon}
              >
                <Popup>
                  <BusinessPopup>
                    <BusinessName>{business.name}</BusinessName>

                    {business.forSale && (
                      <ForSaleTag>À Venda</ForSaleTag>
                    )}

                    <BusinessInfo>
                      <InfoItem>
                        <InfoLabel>Morada:</InfoLabel>
                        <InfoValue>{business.address}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Telefone:</InfoLabel>
                        <InfoValue>{business.phone}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Setor:</InfoLabel>
                        <InfoValue>{businessSectors[business.sector]?.namePortuguese}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Área:</InfoLabel>
                        <InfoValue>{businessAreas[business.area]?.namePortuguese}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Freguesia:</InfoLabel>
                        <InfoValue>{business.parish}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Avaliação:</InfoLabel>
                        <InfoValue>{business.rating.toFixed(1)}/5.0</InfoValue>
                      </InfoItem>

                      {business.forSale && (
                        <>
                          <InfoItem>
                            <InfoLabel>Tipo:</InfoLabel>
                            <InfoValue>
                              {business.saleType === 'business' ? 'Negócio' : 'Imóvel'}
                            </InfoValue>
                          </InfoItem>
                          <InfoItem>
                            <InfoLabel>Preço:</InfoLabel>
                            <InfoValue>{business.price.toLocaleString()}€</InfoValue>
                          </InfoItem>
                        </>
                      )}
                    </BusinessInfo>
                  </BusinessPopup>
                </Popup>
              </Marker>
            ))}
          </MapContainer>
        </MapWrapper>
      </ContentContainer>
    </PageContainer>
  );
};

