import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { businessSectors, businessAreas } from '../data/businessData';

// Lista de freguesias de Lisboa
const lisboaParishes = [
  'Ajuda', 'Alcântara', 'Alvalade', 'Areeiro', 'Arroios', 'Avenidas Novas', 
  'Beato', 'Belém', 'Benfica', 'Campo de Ourique', 'Campolide', 'Carnide', 
  'Estrela', 'Lumiar', 'Marvila', 'Misericórdia', 'Olivais', 'Parque das Nações', 
  'Penha de França', 'Santa Clara', 'Santa Maria Maior', 'Santo António', 
  'São Domingos de Benfica', 'São Vicente'
];

// Dados mockados de estabelecimentos em Lisboa
const mockBusinesses = [
  {
    id: 1,
    name: "Café Lisboa",
    position: { lat: 38.7173, lng: -9.1378 },
    sector: "food",
    area: "cafe",
    parish: "Santa Maria Maior",
    rating: 4.2,
    forSale: false
  },
  {
    id: 2,
    name: "Restaurante Tejo",
    position: { lat: 38.7103, lng: -9.1448 },
    sector: "food",
    area: "restaurant",
    parish: "Misericórdia",
    rating: 4.5,
    forSale: true,
    saleType: "business",
    price: 120000
  },
  {
    id: 3,
    name: "Loja de Roupas Chiado",
    position: { lat: 38.7113, lng: -9.1408 },
    sector: "retail",
    area: "clothing",
    parish: "Santa Maria Maior",
    rating: 4.0,
    forSale: false
  },
  {
    id: 4,
    name: "Padaria Tradicional",
    position: { lat: 38.7153, lng: -9.1428 },
    sector: "food",
    area: "bakery",
    parish: "Misericórdia",
    rating: 4.7,
    forSale: true,
    saleType: "property",
    price: 350000
  },
  {
    id: 5,
    name: "Mercearia do Bairro",
    position: { lat: 38.7193, lng: -9.1398 },
    sector: "retail",
    area: "grocery",
    parish: "Santa Maria Maior",
    rating: 4.3,
    forSale: false
  }
];

const BusinessMap = () => {
  const [businesses, setBusinesses] = useState(mockBusinesses);
  const [filteredBusinesses, setFilteredBusinesses] = useState(mockBusinesses);
  const [selectedBusiness, setSelectedBusiness] = useState(null);
  const [selectedSector, setSelectedSector] = useState("");
  const [selectedArea, setSelectedArea] = useState("");
  const [selectedParish, setSelectedParish] = useState("");
  const [radius, setRadius] = useState(2);
  const [showOnlyForSale, setShowOnlyForSale] = useState(false);
  const [averageRatings, setAverageRatings] = useState({});

  // Filtrar estabelecimentos com base nos critérios selecionados
  useEffect(() => {
    let filtered = businesses;

    if (selectedSector) {
      filtered = filtered.filter(business => business.sector === selectedSector);
    }

    if (selectedArea) {
      filtered = filtered.filter(business => business.area === selectedArea);
    }

    if (selectedParish) {
      filtered = filtered.filter(business => business.parish === selectedParish);
    }

    if (showOnlyForSale) {
      filtered = filtered.filter(business => business.forSale);
    }

    setFilteredBusinesses(filtered);
  }, [businesses, selectedSector, selectedArea, selectedParish, radius, showOnlyForSale]);

  // Calcular médias de avaliação por setor e área
  useEffect(() => {
    const calculateAverages = () => {
      const sectorRatings = {};
      const areaRatings = {};

      businesses.forEach(business => {
        if (business.sector) {
          if (!sectorRatings[business.sector]) {
            sectorRatings[business.sector] = { sum: 0, count: 0 };
          }
          sectorRatings[business.sector].sum += business.rating;
          sectorRatings[business.sector].count += 1;
        }

        if (business.area) {
          if (!areaRatings[business.area]) {
            areaRatings[business.area] = { sum: 0, count: 0 };
          }
          areaRatings[business.area].sum += business.rating;
          areaRatings[business.area].count += 1;
        }
      });

      const averages = {
        sectors: {},
        areas: {}
      };

      Object.keys(sectorRatings).forEach(sector => {
        averages.sectors[sector] = sectorRatings[sector].sum / sectorRatings[sector].count;
      });

      Object.keys(areaRatings).forEach(area => {
        averages.areas[area] = areaRatings[area].sum / areaRatings[area].count;
      });

      setAverageRatings(averages);
    };

    calculateAverages();
  }, [businesses]);

  // Função para obter o nome do setor
  const getSectorName = (sectorId) => {
    const sector = businessSectors.find(s => s.id === sectorId);
    return sector ? sector.namePortuguese : sectorId;
  };

  // Função para obter o nome da área
  const getAreaName = (areaId) => {
    const area = businessAreas.find(a => a.id === areaId);
    return area ? area.namePortuguese : areaId;
  };

  return (
    <PageContainer>
      <SidePanel>
        <PanelTitle>Filtros</PanelTitle>

        <FilterSection>
          <FilterLabel>Setor</FilterLabel>
          <Select 
            value={selectedSector} 
            onChange={(e) => setSelectedSector(e.target.value)}
          >
            <option value="">Todos os setores</option>
            {businessSectors.map(sector => (
              <option key={sector.id} value={sector.id}>
                {sector.namePortuguese}
              </option>
            ))}
          </Select>
        </FilterSection>

        <FilterSection>
          <FilterLabel>Área de Negócio</FilterLabel>
          <Select 
            value={selectedArea} 
            onChange={(e) => setSelectedArea(e.target.value)}
            disabled={!selectedSector}
          >
            <option value="">Todas as áreas</option>
            {businessAreas
              .filter(area => !selectedSector || area.sectorId === selectedSector)
              .map(area => (
                <option key={area.id} value={area.id}>
                  {area.namePortuguese}
                </option>
              ))
            }
          </Select>
        </FilterSection>

        <FilterSection>
          <FilterLabel>Freguesia</FilterLabel>
          <Select 
            value={selectedParish} 
            onChange={(e) => setSelectedParish(e.target.value)}
          >
            <option value="">Todas as freguesias</option>
            {lisboaParishes.map(parish => (
              <option key={parish} value={parish}>
                {parish}
              </option>
            ))}
          </Select>
        </FilterSection>

        <FilterSection>
          <FilterLabel>Raio de Pesquisa (km)</FilterLabel>
          <RangeSlider 
            type="range" 
            min="0.5" 
            max="5" 
            step="0.5" 
            value={radius} 
            onChange={(e) => setRadius(parseFloat(e.target.value))}
          />
          <RangeValue>{radius} km</RangeValue>
        </FilterSection>

        <FilterSection>
          <CheckboxContainer>
            <Checkbox 
              type="checkbox" 
              id="for-sale" 
              checked={showOnlyForSale} 
              onChange={() => setShowOnlyForSale(!showOnlyForSale)}
            />
            <CheckboxLabel htmlFor="for-sale">Apenas à venda</CheckboxLabel>
          </CheckboxContainer>
        </FilterSection>

        <StatsSection>
          <StatsTitle>Médias de Desempenho</StatsTitle>

          {selectedSector && averageRatings.sectors && averageRatings.sectors[selectedSector] && (
            <StatItem>
              <StatLabel>Média do Setor:</StatLabel>
              <StatValue>{averageRatings.sectors[selectedSector].toFixed(1)}</StatValue>
            </StatItem>
          )}

          {selectedArea && averageRatings.areas && averageRatings.areas[selectedArea] && (
            <StatItem>
              <StatLabel>Média da Área:</StatLabel>
              <StatValue>{averageRatings.areas[selectedArea].toFixed(1)}</StatValue>
            </StatItem>
          )}
        </StatsSection>

        <LegendContainer>
          <LegendTitle>Legenda</LegendTitle>
          <LegendItem>
            <LegendIcon forSale={false} />
            <LegendText>Estabelecimento</LegendText>
          </LegendItem>
          <LegendItem>
            <LegendIcon forSale={true} />
            <LegendText>À Venda</LegendText>
          </LegendItem>
        </LegendContainer>
      </SidePanel>

      <MapContainer>
        <MapPlaceholder>
          <h3>Mapa de Comércio</h3>
          <p>Para visualizar o mapa interativo, instale as dependências necessárias:</p>
          <code>npm install react-leaflet leaflet</code>
          <p>Estabelecimentos encontrados: {filteredBusinesses.length}</p>
          <BusinessList>
            {filteredBusinesses.map(business => (
              <BusinessCard key={business.id} forSale={business.forSale}>
                <BusinessCardTitle>{business.name}</BusinessCardTitle>
                {business.forSale && <ForSaleTag>À Venda</ForSaleTag>}
                <BusinessCardInfo>
                  <InfoItem>
                    <InfoLabel>Setor:</InfoLabel>
                    <InfoValue>{getSectorName(business.sector)}</InfoValue>
                  </InfoItem>
                  <InfoItem>
                    <InfoLabel>Área:</InfoLabel>
                    <InfoValue>{getAreaName(business.area)}</InfoValue>
                  </InfoItem>
                  <InfoItem>
                    <InfoLabel>Freguesia:</InfoLabel>
                    <InfoValue>{business.parish}</InfoValue>
                  </InfoItem>
                  <InfoItem>
                    <InfoLabel>Avaliação:</InfoLabel>
                    <InfoValue>{business.rating.toFixed(1)}</InfoValue>
                  </InfoItem>
                  {business.forSale && (
                    <>
                      <InfoItem>
                        <InfoLabel>Tipo:</InfoLabel>
                        <InfoValue>
                          {business.saleType === 'business' ? 'Negócio' : 'Imóvel'}
                        </InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Preço:</InfoLabel>
                        <InfoValue>{business.price.toLocaleString('pt-PT')}€</InfoValue>
                      </InfoItem>
                    </>
                  )}
                </BusinessCardInfo>
              </BusinessCard>
            ))}
          </BusinessList>
        </MapPlaceholder>
      </MapContainer>
    </PageContainer>
  );
};

const PageContainer = styled.div`
  display: flex;
  height: calc(100vh - 80px);
`;

const SidePanel = styled.div`
  width: 300px;
  padding: 20px;
  background-color: white;
  border-right: 1px solid #e0e6ed;
  overflow-y: auto;
`;

const PanelTitle = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 20px;
`;

const FilterSection = styled.div`
  margin-bottom: 20px;
`;

const FilterLabel = styled.label`
  display: block;
  font-size: 14px;
  font-weight: 500;
  color: #34495e;
  margin-bottom: 8px;
`;

const Select = styled.select`
  width: 100%;
  padding: 10px;
  border: 1px solid #dcdfe6;
  border-radius: 4px;
  font-size: 14px;
  color: #2c3e50;
  background-color: white;

  &:disabled {
    background-color: #f5f7fa;
    cursor: not-allowed;
  }
`;

const RangeSlider = styled.input`
  width: 100%;
  margin-bottom: 5px;
`;

const RangeValue = styled.div`
  text-align: center;
  font-size: 14px;
  color: #2c3e50;
`;

const CheckboxContainer = styled.div`
  display: flex;
  align-items: center;
`;

const Checkbox = styled.input`
  margin-right: 8px;
`;

const CheckboxLabel = styled.label`
  font-size: 14px;
  color: #2c3e50;
`;

const StatsSection = styled.div`
  margin-top: 20px;
  padding-top: 20px;
  border-top: 1px solid #e0e6ed;
`;

const StatsTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 10px;
`;

const StatItem = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 8px;
`;

const StatLabel = styled.span`
  font-size: 14px;
  color: #7f8c8d;
`;

const StatValue = styled.span`
  font-size: 14px;
  font-weight: 600;
  color: #2c3e50;
`;

const LegendContainer = styled.div`
  margin-top: 15px;
`;

const LegendTitle = styled.h4`
  font-size: 14px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 8px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const LegendIcon = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 50%;
  margin-right: 8px;
  background-color: ${props => props.forSale ? '#EA4335' : '#4285F4'};
`;

const LegendText = styled.span`
  font-size: 13px;
  color: #2c3e50;
`;

const MapContainer = styled.div`
  flex: 1;
  position: relative;
`;

const MapPlaceholder = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  background-color: #f5f7fa;
  padding: 20px;
  text-align: center;

  h3 {
    font-size: 24px;
    color: #2c3e50;
    margin-bottom: 15px;
  }

  p {
    font-size: 16px;
    color: #7f8c8d;
    margin-bottom: 10px;
  }

  code {
    display: block;
    padding: 10px;
    background-color: #34495e;
    color: white;
    border-radius: 4px;
    margin-bottom: 20px;
    font-family: monospace;
  }
`;

const BusinessList = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 15px;
  width: 100%;
  max-height: 60%;
  overflow-y: auto;
  padding: 10px;
`;

const BusinessCard = styled.div`
  background-color: white;
  border-radius: 8px;
  padding: 15px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  border-left: 4px solid ${props => props.forSale ? '#EA4335' : '#4285F4'};
`;

const BusinessCardTitle = styled.h4`
  font-size: 16px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 5px;
`;

const ForSaleTag = styled.div`
  display: inline-block;
  background-color: #EA4335;
  color: white;
  font-size: 12px;
  font-weight: 500;
  padding: 3px 8px;
  border-radius: 4px;
  margin-bottom: 10px;
`;

const BusinessCardInfo = styled.div`
  font-size: 13px;
`;

const InfoItem = styled.div`
  display: flex;
  margin-bottom: 5px;
`;

const InfoLabel = styled.span`
  font-weight: 500;
  color: #7f8c8d;
  width: 70px;
`;

const InfoValue = styled.span`
  color: #2c3e50;
`;

export default BusinessMap;
