import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const Diagnosis = () => {
  const [formData, setFormData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [kpis, setKpis] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    // Recuperar dados do localStorage
    const savedData = localStorage.getItem('registrationFormData');

    if (savedData) {
      const parsedData = JSON.parse(savedData);
      setFormData(parsedData);

      // Calcular KPIs
      calculateKPIs(parsedData);
    } else {
      // Redirecionar para o registro se não houver dados
      navigate('/');
    }

    setLoading(false);
  }, [navigate]);

  const calculateKPIs = (data) => {
    // Converter strings para números para cálculos
    const sales = parseFloat(data.sales) || 0;
    const cogs = parseFloat(data.costOfGoodsSold) || 0;
    const opCosts = parseFloat(data.operationalCosts) || 0;
    const interest = parseFloat(data.interest) || 0;
    const taxes = parseFloat(data.taxes) || 0;
    const debt = parseFloat(data.debt) || 0;
    const assets = parseFloat(data.assets) || 0;
    const rentPrice = parseFloat(data.rentPrice) || 0;
    const employees = parseFloat(data.employeeCount) || 1;
    const customers = parseFloat(data.customerCount) || 1;

    // Cálculos de KPIs
    const grossProfit = sales - cogs;
    const operationalProfit = grossProfit - opCosts;
    const netProfit = operationalProfit - interest - taxes;

    const calculatedKpis = {
      // Indicadores de Rentabilidade
      grossMargin: sales > 0 ? (grossProfit / sales * 100).toFixed(2) : 0,
      operationalMargin: sales > 0 ? (operationalProfit / sales * 100).toFixed(2) : 0,
      netMargin: sales > 0 ? (netProfit / sales * 100).toFixed(2) : 0,
      roa: assets > 0 ? (netProfit / assets * 100).toFixed(2) : 0,

      // Indicadores de Eficiência
      assetTurnover: assets > 0 ? (sales / assets).toFixed(2) : 0,
      revenuePerEmployee: (sales / employees).toFixed(2),
      profitPerEmployee: (netProfit / employees).toFixed(2),
      revenuePerCustomer: (sales / customers).toFixed(2),

      // Indicadores de Endividamento
      debtRatio: assets > 0 ? (debt / assets * 100).toFixed(2) : 0,
      interestCoverageRatio: interest > 0 ? (operationalProfit / interest).toFixed(2) : 'N/A',

      // Indicadores de Liquidez
      rentEffortRatio: data.propertyStatus === 'rented' && sales > 0 ? 
        ((rentPrice * 12) / sales * 100).toFixed(2) : 'N/A',
      operationalRentEffort: data.propertyStatus === 'rented' && opCosts > 0 ? 
        ((rentPrice * 12) / opCosts * 100).toFixed(2) : 'N/A',

      // Indicadores de Eficiência Operacional
      operationalCostRatio: sales > 0 ? (opCosts / sales * 100).toFixed(2) : 0,
      cogsRatio: sales > 0 ? (cogs / sales * 100).toFixed(2) : 0,
      taxBurden: sales > 0 ? (taxes / sales * 100).toFixed(2) : 0,
    };

    setKpis(calculatedKpis);
  };

  if (loading) {
    return <LoadingContainer>A carregar o diagnóstico...</LoadingContainer>;
  }

  if (!formData) {
    return <ErrorContainer>Não foram encontrados dados de registo. Por favor, complete o registo primeiro.</ErrorContainer>;
  }

  // Encontrar o nome do setor e área de negócio
  const getSectorName = () => {
    try {
      // Importar dinamicamente os dados (não é a melhor prática, mas funciona para este exemplo)
      const { businessSectors } = require('../data/businessData');
      const sector = businessSectors.find(s => s.id === formData.businessSector);
      return sector ? sector.namePortuguese : formData.businessSector;
    } catch (error) {
      return formData.businessSector;
    }
  };

  const getAreaName = () => {
    try {
      const { businessAreas } = require('../data/businessData');
      const area = businessAreas.find(a => a.id === formData.businessArea);
      return area ? area.namePortuguese : formData.businessArea;
    } catch (error) {
      return formData.businessArea;
    }
  };

  return (
    <PageContainer>
      <MainContent>
        <DiagnosisTitle>Diagnóstico da Sua Empresa</DiagnosisTitle>

        <SectionCard>
          <SectionTitle>Informações da Empresa</SectionTitle>
          <InfoGrid>
            <InfoItem>
              <InfoLabel>Setor:</InfoLabel>
              <InfoValue>{getSectorName()}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Área de Negócio:</InfoLabel>
              <InfoValue>{getAreaName()}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Localização:</InfoLabel>
              <InfoValue>{formData.location}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Número de Funcionários:</InfoLabel>
              <InfoValue>{formData.employeeCount}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Idade do Negócio:</InfoLabel>
              <InfoValue>{formData.businessAge} anos</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Tipo de Propriedade:</InfoLabel>
              <InfoValue>{formData.propertyStatus === 'owned' ? 'Própria' : 'Arrendada'}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Email:</InfoLabel>
              <InfoValue>{formData.email}</InfoValue>
            </InfoItem>
            <InfoItem>
              <InfoLabel>Telefone:</InfoLabel>
              <InfoValue>{formData.phone}</InfoValue>
            </InfoItem>
          </InfoGrid>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Indicadores Financeiros</SectionTitle>
          <KpiGrid>
            <KpiCard>
              <KpiTitle>Margem Bruta</KpiTitle>
              <KpiValue>{kpis.grossMargin}%</KpiValue>
              <KpiDescription>Percentagem das vendas que sobra após cobrir o custo das mercadorias vendidas</KpiDescription>
            </KpiCard>

            <KpiCard>
              <KpiTitle>Margem Operacional</KpiTitle>
              <KpiValue>{kpis.operationalMargin}%</KpiValue>
              <KpiDescription>Percentagem das vendas que sobra após cobrir todos os custos operacionais</KpiDescription>
            </KpiCard>

            <KpiCard>
              <KpiTitle>Margem Líquida</KpiTitle>
              <KpiValue>{kpis.netMargin}%</KpiValue>
              <KpiDescription>Percentagem das vendas que sobra como lucro líquido</KpiDescription>
            </KpiCard>

            <KpiCard>
              <KpiTitle>Retorno sobre Ativos (ROA)</KpiTitle>
              <KpiValue>{kpis.roa}%</KpiValue>
              <KpiDescription>Eficiência na utilização dos ativos para gerar lucro</KpiDescription>
            </KpiCard>

            <KpiCard>
              <KpiTitle>Rácio de Endividamento</KpiTitle>
              <KpiValue>{kpis.debtRatio}%</KpiValue>
              <KpiDescription>Percentagem dos ativos financiados por dívida</KpiDescription>
            </KpiCard>

            <KpiCard>
              <KpiTitle>Cobertura de Juros</KpiTitle>
              <KpiValue>{kpis.interestCoverageRatio}</KpiValue>
              <KpiDescription>Capacidade de pagar juros com o lucro operacional</KpiDescription>
            </KpiCard>

            {formData.propertyStatus === 'rented' && (
              <KpiCard>
                <KpiTitle>Taxa de Esforço (Renda)</KpiTitle>
                <KpiValue>{kpis.rentEffortRatio}%</KpiValue>
                <KpiDescription>Percentagem das vendas destinada ao pagamento da renda</KpiDescription>
              </KpiCard>
            )}

            <KpiCard>
              <KpiTitle>Receita por Funcionário</KpiTitle>
              <KpiValue>€{parseFloat(kpis.revenuePerEmployee).toLocaleString('pt-PT')}</KpiValue>
              <KpiDescription>Vendas geradas por cada funcionário</KpiDescription>
            </KpiCard>

            <KpiCard>
              <KpiTitle>Receita por Cliente</KpiTitle>
              <KpiValue>€{parseFloat(kpis.revenuePerCustomer).toLocaleString('pt-PT')}</KpiValue>
              <KpiDescription>Valor médio gerado por cada cliente</KpiDescription>
            </KpiCard>
          </KpiGrid>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Recomendações</SectionTitle>
          <RecommendationsList>
            {parseFloat(kpis.grossMargin) < 30 && (
              <RecommendationItem>
                <RecommendationIcon>💡</RecommendationIcon>
                <RecommendationText>
                  <RecommendationTitle>Melhorar a Margem Bruta</RecommendationTitle>
                  <RecommendationDescription>
                    A sua margem bruta está abaixo da média do setor. Considere renegociar com fornecedores ou ajustar os preços de venda.
                  </RecommendationDescription>
                </RecommendationText>
              </RecommendationItem>
            )}

            {parseFloat(kpis.debtRatio) > 70 && (
              <RecommendationItem>
                <RecommendationIcon>⚠️</RecommendationIcon>
                <RecommendationText>
                  <RecommendationTitle>Reduzir o Endividamento</RecommendationTitle>
                  <RecommendationDescription>
                    O seu rácio de endividamento é elevado. Considere estratégias para reduzir a dívida ou aumentar os ativos.
                  </RecommendationDescription>
                </RecommendationText>
              </RecommendationItem>
            )}

            {formData.propertyStatus === 'rented' && parseFloat(kpis.rentEffortRatio) > 15 && (
              <RecommendationItem>
                <RecommendationIcon>🏢</RecommendationIcon>
                <RecommendationText>
                  <RecommendationTitle>Renegociar Contrato de Arrendamento</RecommendationTitle>
                  <RecommendationDescription>
                    A sua taxa de esforço com a renda é elevada. Considere renegociar o contrato ou procurar um espaço mais económico.
                  </RecommendationDescription>
                </RecommendationText>
              </RecommendationItem>
            )}

            {parseFloat(kpis.operationalCostRatio) > 50 && (
              <RecommendationItem>
                <RecommendationIcon>📉</RecommendationIcon>
                <RecommendationText>
                  <RecommendationTitle>Reduzir Custos Operacionais</RecommendationTitle>
                  <RecommendationDescription>
                    Os seus custos operacionais representam uma percentagem elevada das vendas. Identifique áreas para redução de custos.
                  </RecommendationDescription>
                </RecommendationText>
              </RecommendationItem>
            )}

            <RecommendationItem>
              <RecommendationIcon>📊</RecommendationIcon>
              <RecommendationText>
                <RecommendationTitle>Análise Detalhada</RecommendationTitle>
                <RecommendationDescription>
                  Para uma análise mais detalhada e personalizada, entre em contacto com a nossa equipa de consultores.
                </RecommendationDescription>
              </RecommendationText>
            </RecommendationItem>
          </RecommendationsList>
        </SectionCard>

        <ButtonContainer>
          <ActionButton onClick={() => navigate('/')}>Voltar ao Início</ActionButton>
          <ActionButton primary>Descarregar Relatório PDF</ActionButton>
        </ButtonContainer>
      </MainContent>
    </PageContainer>
  );
};

