import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { businessSectors, businessAreas } from '../data/businessData';

const EditData = () => {
  const [formData, setFormData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState({ text: '', type: '' });
  const navigate = useNavigate();

  useEffect(() => {
    // Recuperar dados do localStorage
    const savedData = localStorage.getItem('registrationFormData');

    if (savedData) {
      setFormData(JSON.parse(savedData));
    } else {
      // Redirecionar para o registro se não houver dados
      navigate('/');
    }

    setLoading(false);
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Salvar dados atualizados no localStorage
    localStorage.setItem('registrationFormData', JSON.stringify(formData));

    // Mostrar mensagem de sucesso
    setMessage({ 
      text: 'Dados atualizados com sucesso!', 
      type: 'success' 
    });

    // Limpar mensagem após 3 segundos
    setTimeout(() => {
      setMessage({ text: '', type: '' });
    }, 3000);
  };

  if (loading) {
    return <LoadingContainer>A carregar os dados...</LoadingContainer>;
  }

  if (!formData) {
    return <ErrorContainer>Não foram encontrados dados de registo. Por favor, complete o registo primeiro.</ErrorContainer>;
  }

  // Filtrar áreas de negócio com base no setor selecionado
  const filteredAreas = businessAreas.filter(
    area => area.sectorId === formData.businessSector
  );

  return (
    <PageContainer>
      <PageTitle>Editar Dados</PageTitle>

      {message.text && (
        <MessageContainer type={message.type}>
          {message.text}
        </MessageContainer>
      )}

      <Form onSubmit={handleSubmit}>
        <SectionCard>
          <SectionTitle>Informações do Negócio</SectionTitle>

          <InputGroup>
            <InputContainer>
              <InputLabel>Setor de Atividade</InputLabel>
              <Select
                name="businessSector"
                value={formData.businessSector}
                onChange={handleChange}
                required
              >
                <option value="">Selecione o setor</option>
                {businessSectors.map(sector => (
                  <option key={sector.id} value={sector.id}>
                    {sector.namePortuguese}
                  </option>
                ))}
              </Select>
            </InputContainer>

            <InputContainer>
              <InputLabel>Área de Negócio</InputLabel>
              <Select
                name="businessArea"
                value={formData.businessArea}
                onChange={handleChange}
                required
                disabled={!formData.businessSector}
              >
                <option value="">Selecione a área</option>
                {filteredAreas.map(area => (
                  <option key={area.id} value={area.id}>
                    {area.namePortuguese}
                  </option>
                ))}
              </Select>
            </InputContainer>
          </InputGroup>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Métricas Estruturais</SectionTitle>

          <InputGroup>
            <InputContainer>
              <InputLabel>Localização</InputLabel>
              <Input
                type="text"
                name="location"
                value={formData.location}
                onChange={handleChange}
                placeholder="Ex: Lisboa"
                required
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Número de Funcionários</InputLabel>
              <Input
                type="number"
                name="employeeCount"
                value={formData.employeeCount}
                onChange={handleChange}
                placeholder="Ex: 10"
                required
                min="1"
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Email do Estabelecimento</InputLabel>
              <Input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Ex: empresa@exemplo.pt"
                required
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Número de Telefone</InputLabel>
              <Input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                placeholder="Ex: 912345678"
                required
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Idade do Negócio (anos)</InputLabel>
              <Input
                type="number"
                name="businessAge"
                value={formData.businessAge}
                onChange={handleChange}
                placeholder="Ex: 5"
                required
                min="0"
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Tipo de Propriedade</InputLabel>
              <RadioGroup>
                <RadioOption>
                  <RadioInput
                    type="radio"
                    name="propertyStatus"
                    value="owned"
                    checked={formData.propertyStatus === 'owned'}
                    onChange={handleChange}
                    required
                  />
                  <RadioLabel>Própria</RadioLabel>
                </RadioOption>
                <RadioOption>
                  <RadioInput
                    type="radio"
                    name="propertyStatus"
                    value="rented"
                    checked={formData.propertyStatus === 'rented'}
                    onChange={handleChange}
                    required
                  />
                  <RadioLabel>Arrendada</RadioLabel>
                </RadioOption>
              </RadioGroup>
            </InputContainer>
          </InputGroup>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Métricas Financeiras</SectionTitle>

          <InputGroup>
            <InputContainer>
              <InputLabel>Vendas (€)</InputLabel>
              <Input
                type="number"
                name="sales"
                value={formData.sales}
                onChange={handleChange}
                placeholder="Ex: 500000"
                required
                min="0"
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Custo da Mercadoria Vendida (€)</InputLabel>
              <Input
                type="number"
                name="costOfGoodsSold"
                value={formData.costOfGoodsSold}
                onChange={handleChange}
                placeholder="Ex: 300000"
                required
                min="0"
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Lucro Bruto (€)</InputLabel>
              <Input
                type="number"
                name="grossProfit"
                value={formData.grossProfit}
                onChange={handleChange}
                placeholder="Ex: 200000"
                required
                min="0"
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Custos Operacionais (€)</InputLabel>
              <Input
                type="number"
                name="operationalCosts"
                value={formData.operationalCosts}
                onChange={handleChange}
                placeholder="Ex: 150000"
                required
                min="0"
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Lucro Operacional (€)</InputLabel>
              <Input
                type="number"
                name="operationalProfit"
                value={formData.operationalProfit}
                onChange={handleChange}
                placeholder="Ex: 50000"
                required
                min="0"
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Juros (€)</InputLabel>
              <Input
                type="number"
                name="interest"
                value={formData.interest}
                onChange={handleChange}
                placeholder="Ex: 10000"
                required
                min="0"
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Impostos (€)</InputLabel>
              <Input
                type="number"
                name="taxes"
                value={formData.taxes}
                onChange={handleChange}
                placeholder="Ex: 20000"
                required
                min="0"
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Lucro Líquido (€)</InputLabel>
              <Input
                type="number"
                name="netProfit"
                value={formData.netProfit}
                onChange={handleChange}
                placeholder="Ex: 20000"
                required
                min="0"
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Dívida Total (€)</InputLabel>
              <Input
                type="number"
                name="debt"
                value={formData.debt}
                onChange={handleChange}
                placeholder="Ex: 200000"
                required
                min="0"
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Ativo Total (€)</InputLabel>
              <Input
                type="number"
                name="assets"
                value={formData.assets}
                onChange={handleChange}
                placeholder="Ex: 800000"
                required
                min="0"
              />
            </InputContainer>
          </InputGroup>

          <InputGroup>
            <InputContainer>
              <InputLabel>Preço do Arrendamento (€/mês)</InputLabel>
              <Input
                type="number"
                name="rentPrice"
                value={formData.rentPrice}
                onChange={handleChange}
                placeholder="Ex: 2000"
                required={formData.propertyStatus === 'rented'}
                min="0"
                disabled={formData.propertyStatus !== 'rented'}
              />
            </InputContainer>

            <InputContainer>
              <InputLabel>Número de Clientes (anual)</InputLabel>
              <Input
                type="number"
                name="customerCount"
                value={formData.customerCount}
                onChange={handleChange}
                placeholder="Ex: 5000"
                required
                min="0"
              />
            </InputContainer>
          </InputGroup>
        </SectionCard>

        <ButtonContainer>
          <ActionButton type="button" onClick={() => navigate('/diagnosis')}>Cancelar</ActionButton>
          <ActionButton type="submit" primary>Guardar Alterações</ActionButton>
        </ButtonContainer>
      </Form>
    </PageContainer>
  );
};

