import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { 
  Chart as ChartJS, 
  CategoryScale, 
  LinearScale, 
  BarElement, 
  Title, 
  Tooltip, 
  Legend 
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

// Registrar os componentes do Chart.js
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

// Médias do setor (hardcoded para demonstração)
const sectorAverages = {
  retail: {
    grossMargin: 35,
    operationalMargin: 18,
    netMargin: 8,
    rentEffortRatio: 5,
    operationalRentEffort: 15,
    interestCoverageRatio: 5,
    debtRatio: 40
  },
  manufacturing: {
    grossMargin: 40,
    operationalMargin: 20,
    netMargin: 10,
    rentEffortRatio: 3,
    operationalRentEffort: 10,
    interestCoverageRatio: 8,
    debtRatio: 45
  },
  services: {
    grossMargin: 60,
    operationalMargin: 25,
    netMargin: 15,
    rentEffortRatio: 8,
    operationalRentEffort: 20,
    interestCoverageRatio: 10,
    debtRatio: 30
  },
  technology: {
    grossMargin: 70,
    operationalMargin: 30,
    netMargin: 18,
    rentEffortRatio: 6,
    operationalRentEffort: 15,
    interestCoverageRatio: 12,
    debtRatio: 25
  },
  food: {
    grossMargin: 45,
    operationalMargin: 15,
    netMargin: 7,
    rentEffortRatio: 7,
    operationalRentEffort: 18,
    interestCoverageRatio: 4,
    debtRatio: 50
  },
  construction: {
    grossMargin: 30,
    operationalMargin: 12,
    netMargin: 6,
    rentEffortRatio: 4,
    operationalRentEffort: 12,
    interestCoverageRatio: 3,
    debtRatio: 60
  },
  // Valor padrão para setores não especificados
  default: {
    grossMargin: 40,
    operationalMargin: 20,
    netMargin: 10,
    rentEffortRatio: 5,
    operationalRentEffort: 15,
    interestCoverageRatio: 6,
    debtRatio: 40
  }
};

const Diagnosis = () => {
  const [yearData, setYearData] = useState({
    2023: null,
    2024: null,
    2025: null
  });
  const [loading, setLoading] = useState(true);
  const [kpis, setKpis] = useState({});
  const [sectorAvg, setSectorAvg] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Recuperar dados do localStorage
    const data2023 = localStorage.getItem('data2023');
    const data2024 = localStorage.getItem('data2024');
    const data2025 = localStorage.getItem('registrationFormData');

    if (data2025) {
      const parsedData2025 = JSON.parse(data2025);
      const parsedData2024 = data2024 ? JSON.parse(data2024) : null;
      const parsedData2023 = data2023 ? JSON.parse(data2023) : null;

      setYearData({
        2023: parsedData2023,
        2024: parsedData2024,
        2025: parsedData2025
      });

      // Calcular KPIs para o ano atual
      calculateKPIs(parsedData2025);

      // Definir médias do setor com base no setor do negócio
      const sectorKey = getSectorKey(parsedData2025.businessSector);
      setSectorAvg(sectorAverages[sectorKey] || sectorAverages.default);
    } else {
      // Redirecionar para o registro se não houver dados
      navigate('/');
    }

    setLoading(false);
  }, [navigate]);

  // Função para mapear o ID do setor para a chave no objeto sectorAverages
  const getSectorKey = (sectorId) => {
    const sectorMap = {
      'retail': 'retail',
      'manufacturing': 'manufacturing',
      'services': 'services',
      'technology': 'technology',
      'food': 'food',
      'construction': 'construction'
    };

    return sectorMap[sectorId] || 'default';
  };

  const calculateKPIs = (data) => {
    // Converter strings para números para cálculos
    const sales = parseFloat(data.sales) || 0;
    const cogs = parseFloat(data.costOfGoodsSold) || 0;
    const opCosts = parseFloat(data.operationalCosts) || 0;
    const interest = parseFloat(data.interest) || 0;
    const taxes = parseFloat(data.taxes) || 0;
    const debt = parseFloat(data.debt) || 0;
    const assets = parseFloat(data.assets) || 0;
    const rentPrice = parseFloat(data.rentPrice) || 0;

    // Cálculos de KPIs
    const grossProfit = sales - cogs;
    const operationalProfit = grossProfit - opCosts;
    const netProfit = operationalProfit - interest - taxes;

    const calculatedKpis = {
      // Valores absolutos
      sales,
      cogs,
      grossProfit,
      opCosts,
      operationalProfit,
      interest,
      taxes,
      netProfit,
      debt,
      assets,
      rentPrice,

      // Indicadores de Rentabilidade
      grossMargin: sales > 0 ? (grossProfit / sales * 100).toFixed(2) : 0,
      operationalMargin: sales > 0 ? (operationalProfit / sales * 100).toFixed(2) : 0,
      netMargin: sales > 0 ? (netProfit / sales * 100).toFixed(2) : 0,

      // Indicadores de Eficiência
      rentEffortRatio: sales > 0 ? ((rentPrice * 12) / sales * 100).toFixed(2) : 0,
      operationalRentEffort: opCosts > 0 ? ((rentPrice * 12) / opCosts * 100).toFixed(2) : 0,
      interestCoverageRatio: interest > 0 ? (operationalProfit / interest).toFixed(2) : 'N/A',
      debtRatio: assets > 0 ? (debt / assets * 100).toFixed(2) : 0,
    };

    setKpis(calculatedKpis);
  };

  // Preparar dados para o gráfico de evolução anual
  const prepareYearlyData = () => {
    const years = ['2023', '2024', '2025'];

    // Calcular valores para cada ano
    const yearlyValues = years.map(year => {
      if (!yearData[year]) return { sales: 0, grossProfit: 0, operationalProfit: 0, netProfit: 0 };

      const data = yearData[year];
      const sales = parseFloat(data.sales) || 0;
      const cogs = parseFloat(data.costOfGoodsSold) || 0;
      const opCosts = parseFloat(data.operationalCosts) || 0;
      const interest = parseFloat(data.interest) || 0;
      const taxes = parseFloat(data.taxes) || 0;

      const grossProfit = sales - cogs;
      const operationalProfit = grossProfit - opCosts;
      const netProfit = operationalProfit - interest - taxes;

      return { sales, grossProfit, operationalProfit, netProfit };
    });

    return {
      labels: years,
      datasets: [
        {
          label: 'Vendas',
          data: yearlyValues.map(v => v.sales),
          backgroundColor: 'rgba(54, 162, 235, 0.7)',
          borderColor: 'rgba(54, 162, 235, 1)',
          borderWidth: 1
        },
        {
          label: 'Lucro Bruto',
          data: yearlyValues.map(v => v.grossProfit),
          backgroundColor: 'rgba(75, 192, 192, 0.7)',
          borderColor: 'rgba(75, 192, 192, 1)',
          borderWidth: 1
        },
        {
          label: 'Lucro Operacional',
          data: yearlyValues.map(v => v.operationalProfit),
          backgroundColor: 'rgba(255, 206, 86, 0.7)',
          borderColor: 'rgba(255, 206, 86, 1)',
          borderWidth: 1
        },
        {
          label: 'Lucro Líquido',
          data: yearlyValues.map(v => v.netProfit),
          backgroundColor: 'rgba(153, 102, 255, 0.7)',
          borderColor: 'rgba(153, 102, 255, 1)',
          borderWidth: 1
        }
      ]
    };
  };

  // Preparar dados para o gráfico de comparação com o setor
  const prepareMarginComparisonData = () => {
    if (!kpis || !sectorAvg) return null;

    return {
      labels: ['Margem Bruta', 'Margem Operacional', 'Margem Líquida'],
      datasets: [
        {
          label: 'Sua Empresa',
          data: [
            parseFloat(kpis.grossMargin),
            parseFloat(kpis.operationalMargin),
            parseFloat(kpis.netMargin)
          ],
          backgroundColor: 'rgba(54, 162, 235, 0.7)',
          borderColor: 'rgba(54, 162, 235, 1)',
          borderWidth: 1
        },
        {
          label: 'Média do Setor',
          data: [
            sectorAvg.grossMargin,
            sectorAvg.operationalMargin,
            sectorAvg.netMargin
          ],
          backgroundColor: 'rgba(255, 99, 132, 0.7)',
          borderColor: 'rgba(255, 99, 132, 1)',
          borderWidth: 1
        }
      ]
    };
  };

  // Preparar dados para o gráfico de indicadores financeiros
  const prepareFinancialIndicatorsData = () => {
    if (!kpis || !sectorAvg) return null;

    return {
      labels: ['Taxa de Esforço', 'Renda/Custos Op.', 'Cobertura de Juros', 'Dívida/Ativo'],
      datasets: [
        {
          label: 'Sua Empresa',
          data: [
            parseFloat(kpis.rentEffortRatio),
            parseFloat(kpis.operationalRentEffort),
            kpis.interestCoverageRatio === 'N/A' ? 0 : parseFloat(kpis.interestCoverageRatio),
            parseFloat(kpis.debtRatio)
          ],
          backgroundColor: 'rgba(54, 162, 235, 0.7)',
          borderColor: 'rgba(54, 162, 235, 1)',
          borderWidth: 1
        },
        {
          label: 'Média do Setor',
          data: [
            sectorAvg.rentEffortRatio,
            sectorAvg.operationalRentEffort,
            sectorAvg.interestCoverageRatio,
            sectorAvg.debtRatio
          ],
          backgroundColor: 'rgba(255, 99, 132, 0.7)',
          borderColor: 'rgba(255, 99, 132, 1)',
          borderWidth: 1
        }
      ]
    };
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: false
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  if (loading) {
    return <LoadingContainer>A carregar a análise...</LoadingContainer>;
  }

  if (!yearData[2025]) {
    return <ErrorContainer>Não foram encontrados dados de registo. Por favor, complete o registo primeiro.</ErrorContainer>;
  }

  const yearlyData = prepareYearlyData();
  const marginComparisonData = prepareMarginComparisonData();
  const financialIndicatorsData = prepareFinancialIndicatorsData();

  return (
    <PageContainer>
      <MainContent>
        <DiagnosisTitle>Análise da Sua Empresa</DiagnosisTitle>

        <SectionCard>
          <SectionTitle>Evolução Financeira (2023-2025)</SectionTitle>
          <ChartContainer>
            <Bar data={yearlyData} options={chartOptions} height={300} />
          </ChartContainer>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Comparação de Margens com o Setor</SectionTitle>
          <ChartContainer>
            <Bar data={marginComparisonData} options={chartOptions} height={300} />
          </ChartContainer>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Indicadores Financeiros</SectionTitle>
          <ChartContainer>
            <Bar data={financialIndicatorsData} options={chartOptions} height={300} />
          </ChartContainer>
        </SectionCard>

        <SectionCard>
          <SectionTitle>Resumo Financeiro (2025)</SectionTitle>
          <FinancialSummary>
            <SummaryTable>
              <thead>
                <tr>
                  <th>Indicador</th>
                  <th>Valor</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Vendas</td>
                  <td>{formatCurrency(kpis.sales)}</td>
                </tr>
                <tr>
                  <td>Custo da Mercadoria</td>
                  <td>{formatCurrency(kpis.cogs)}</td>
                </tr>
                <tr className="highlight">
                  <td>Lucro Bruto</td>
                  <td>{formatCurrency(kpis.grossProfit)}</td>
                </tr>
                <tr className="ratio">
                  <td>Margem Bruta</td>
                  <td>{kpis.grossMargin}%</td>
                </tr>
                <tr>
                  <td>Custos Operacionais</td>
                  <td>{formatCurrency(kpis.opCosts)}</td>
                </tr>
                <tr className="highlight">
                  <td>Lucro Operacional</td>
                  <td>{formatCurrency(kpis.operationalProfit)}</td>
                </tr>
                <tr className="ratio">
                  <td>Margem Operacional</td>
                  <td>{kpis.operationalMargin}%</td>
                </tr>
                <tr>
                  <td>Juros</td>
                  <td>{formatCurrency(kpis.interest)}</td>
                </tr>
                <tr>
                  <td>Impostos</td>
                  <td>{formatCurrency(kpis.taxes)}</td>
                </tr>
                <tr className="highlight">
                  <td>Lucro Líquido</td>
                  <td>{formatCurrency(kpis.netProfit)}</td>
                </tr>
                <tr className="ratio">
                  <td>Margem Líquida</td>
                  <td>{kpis.netMargin}%</td>
                </tr>
              </tbody>
            </SummaryTable>

            <SummaryTable>
              <thead>
                <tr>
                  <th>Indicador</th>
                  <th>Valor</th>
                  <th>Média do Setor</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>Taxa de Esforço (Renda/Vendas)</td>
                  <td>{kpis.rentEffortRatio}%</td>
                  <td>{sectorAvg.rentEffortRatio}%</td>
                </tr>
                <tr>
                  <td>Renda/Custos Operacionais</td>
                  <td>{kpis.operationalRentEffort}%</td>
                  <td>{sectorAvg.operationalRentEffort}%</td>
                </tr>
                <tr>
                  <td>Índice de Cobertura de Juros</td>
                  <td>{kpis.interestCoverageRatio}</td>
                  <td>{sectorAvg.interestCoverageRatio}</td>
                </tr>
                <tr>
                  <td>Dívida/Ativo</td>
                  <td>{kpis.debtRatio}%</td>
                  <td>{sectorAvg.debtRatio}%</td>
                </tr>
              </tbody>
            </SummaryTable>
          </FinancialSummary>
        </SectionCard>
      </MainContent>
    </PageContainer>
  );
};

// Função auxiliar para formatar valores monetários
const formatCurrency = (value) => {
  return new Intl.NumberFormat('pt-PT', {
    style: 'currency',
    currency: 'EUR',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0
  }).format(value);
};

// Styled Components
const PageContainer = styled.div`
  padding: 40px;
  background-color: #f5f7fa;
  min-height: 100vh;
`;

const MainContent = styled.main`
  max-width: 1200px;
  margin: 0 auto;
`;

const DiagnosisTitle = styled.h1`
  color: #2c3e50;
  font-size: 36px;
  font-weight: 700;
  margin-bottom: 30px;
  font-family: 'Playfair Display', serif;
`;

const SectionCard = styled.div`
  background-color: white;
  border-radius: 15px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
  padding: 30px;
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #2c3e50;
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 25px;
  padding-bottom: 15px;
  border-bottom: 1px solid #ecf0f1;
`;

const ChartContainer = styled.div`
  height: 400px;
  margin-bottom: 20px;
`;

const FinancialSummary = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 30px;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const SummaryTable = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 20px;

  th, td {
    padding: 12px 15px;
    text-align: left;
    border-bottom: 1px solid #ecf0f1;
  }

  th {
    background-color: #f8f9fa;
    font-weight: 600;
    color: #2c3e50;
  }

  tr.highlight {
    font-weight: 600;
    background-color: #f1f8ff;
  }

  tr.ratio {
    font-style: italic;
    color: #7f8c8d;
  }

  @media (min-width: 769px) {
    flex: 1;
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 24px;
  color: #3498db;
`;

const ErrorContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 20px;
  color: #e74c3c;
  text-align: center;
  padding: 0 20px;
`;

export default Diagnosis;
