import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';

const Diagnosis = () => {
  const [formData, setFormData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [kpis, setKpis] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    // Recuperar dados do localStorage
    const savedData = localStorage.getItem('registrationFormData');

    if (savedData) {
      const parsedData = JSON.parse(savedData);
      setFormData(parsedData);

      // Calcular KPIs
      calculateKPIs(parsedData);
    } else {
      // Redirecionar para o registro se não houver dados
      navigate('/');
    }

    setLoading(false);
  }, [navigate]);

  const calculateKPIs = (data) => {
    // Converter strings para números para cálculos
    const sales = parseFloat(data.sales) || 0;
    const cogs = parseFloat(data.costOfGoodsSold) || 0;
    const opCosts = parseFloat(data.operationalCosts) || 0;
    const interest = parseFloat(data.interest) || 0;
    const taxes = parseFloat(data.taxes) || 0;
    const debt = parseFloat(data.debt) || 0;
    const assets = parseFloat(data.assets) || 0;
    const rentPrice = parseFloat(data.rentPrice) || 0;
    const employees = parseFloat(data.employeeCount) || 1;
    const customers = parseFloat(data.customerCount) || 1;

    // Cálculos de KPIs
    const grossProfit = sales - cogs;
    const operationalProfit = grossProfit - opCosts;
    const netProfit = operationalProfit - interest - taxes;

    const calculatedKpis = {
      // Indicadores de Rentabilidade
      grossMargin: sales > 0 ? (grossProfit / sales * 100).toFixed(2) : 0,
      operationalMargin: sales > 0 ? (operationalProfit / sales * 100).toFixed(2) : 0,
      netMargin: sales > 0 ? (netProfit / sales * 100).toFixed(2) : 0,
      roa: assets > 0 ? (netProfit / assets * 100).toFixed(2) : 0,

      // Indicadores de Eficiência
      assetTurnover: assets > 0 ? (sales / assets).toFixed(2) : 0,
      revenuePerEmployee: (sales / employees).toFixed(2),
      profitPerEmployee: (netProfit / employees).toFixed(2),
      revenuePerCustomer: (sales / customers).toFixed(2),

      // Indicadores de Endividamento
      debtRatio: assets > 0 ? (debt / assets * 100).toFixed(2) : 0,
      interestCoverageRatio: interest > 0 ? (operationalProfit / interest).toFixed(2) : 'N/A',

      // Indicadores de Liquidez
      rentEffortRatio: data.propertyStatus === 'rented' && sales > 0 ? 
        ((rentPrice * 12) / sales * 100).toFixed(2) : 'N/A',
      operationalRentEffort: data.propertyStatus === 'rented' && opCosts > 0 ? 
        ((rentPrice * 12) / opCosts * 100).toFixed(2) : 'N/A',

      // Indicadores de Eficiência Operacional
      operationalCostRatio: sales > 0 ? (opCosts / sales * 100).toFixed(2) : 0,
      cogsRatio: sales > 0 ? (cogs / sales * 100).toFixed(2) : 0,
      taxBurden: sales > 0 ? (taxes / sales * 100).toFixed(2) : 0,
    };

    setKpis(calculatedKpis);
  };

  if (loading) {
    return <LoadingContainer>A carregar a análise...</LoadingContainer>;
  }

  if (!formData) {
    return <ErrorContainer>Não foram encontrados dados de registo. Por favor, complete o registo primeiro.</ErrorContainer>;
  }

  // Encontrar o nome do setor e área de negócio
  const getSectorName = () => {
    try {
      // Importar dinamicamente os dados (não é a melhor prática, mas funciona para este exemplo)
      const { businessSectors } = require('../data/businessData');
      const sector = businessSectors.find(s => s.id === formData.businessSector);
      return sector ? sector.namePortuguese : formData.businessSector;
    } catch (error) {
      return formData.businessSector;
    }
  };

  const getAreaName = () => {
    try {
      const { businessAreas } = require('../data/businessData');
      const area = businessAreas.find(a => a.id === formData.businessArea);
      return area ? area.namePortuguese : formData.businessArea;
    } catch (error) {
      return formData.businessArea;
    }
  };

  return (
    <PageContainer>
      <MainContent>
        <DiagnosisTitle>Análise da Sua Empresa</DiagnosisTitle>

        <SectionCard>
          <SectionTitle>Análise em Desenvolvimento</SectionTitle>
          <EmptyState>
            <EmptyIcon>🔍</EmptyIcon>
            <EmptyText>
              Estamos a preparar a sua análise personalizada.
              <br />
              Em breve, terá acesso a insights detalhados sobre o desempenho da sua empresa.
            </EmptyText>
            <ActionButton primary onClick={() => navigate('/edit-data')}>
              Editar Dados da Empresa
            </ActionButton>
          </EmptyState>
        </SectionCard>
      </MainContent>
    </PageContainer>
  );
};

// Styled Components
const PageContainer = styled.div`
  padding: 40px;
  background-color: #f5f7fa;
  min-height: 100vh;
`;

const MainContent = styled.main`
  max-width: 1200px;
  margin: 0 auto;
`;

const DiagnosisTitle = styled.h1`
  color: #2c3e50;
  font-size: 36px;
  font-weight: 700;
  margin-bottom: 30px;
  font-family: 'Playfair Display', serif;
`;

const SectionCard = styled.div`
  background-color: white;
  border-radius: 15px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
  padding: 30px;
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #2c3e50;
  font-size: 24px;
  font-weight: 600;
  margin-bottom: 25px;
  padding-bottom: 15px;
  border-bottom: 1px solid #ecf0f1;
`;

const EmptyState = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 60px 20px;
  text-align: center;
`;

const EmptyIcon = styled.div`
  font-size: 60px;
  margin-bottom: 20px;
  color: #3498db;
`;

const EmptyText = styled.p`
  color: #7f8c8d;
  font-size: 18px;
  line-height: 1.6;
  max-width: 500px;
  margin-bottom: 30px;
`;

const ActionButton = styled.button`
  background-color: ${props => props.primary ? '#3498db' : '#ecf0f1'};
  color: ${props => props.primary ? 'white' : '#2c3e50'};
  border: none;
  border-radius: 10px;
  padding: 16px 28px;
  font-size: 18px;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s;

  &:hover {
    background-color: ${props => props.primary ? '#2980b9' : '#dfe6e9'};
    transform: translateY(-2px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  }

  &:active {
    transform: translateY(0);
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 24px;
  color: #3498db;
`;

const ErrorContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 20px;
  color: #e74c3c;
  text-align: center;
  padding: 0 20px;
`;

export default Diagnosis;
