import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { MapContainer, TileLayer, Marker, Popup, CircleMarker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { businessSectors, businessAreas } from '../data/businessData';

// Corrigir o problema dos ícones do Leaflet
import L from 'leaflet';
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

// Lista de freguesias de Lisboa
const lisboaParishes = [
  'Ajuda', 'Alcântara', 'Alvalade', 'Areeiro', 'Arroios', 'Avenidas Novas', 
  'Beato', 'Belém', 'Benfica', 'Campo de Ourique', 'Campolide', 'Carnide', 
  'Estrela', 'Lumiar', 'Marvila', 'Misericórdia', 'Olivais', 'Parque das Nações', 
  'Penha de França', 'Santa Clara', 'Santa Maria Maior', 'Santo António', 
  'São Domingos de Benfica', 'São Vicente'
];

// Dados mockados de estabelecimentos em Lisboa
const mockBusinesses = [
  {
    id: 1,
    name: "Café Lisboa",
    position: { lat: 38.7173, lng: -9.1378 },
    sector: "food",
    area: "cafe",
    parish: "Santa Maria Maior",
    rating: 4.2,
    forSale: false
  },
  {
    id: 2,
    name: "Restaurante Tejo",
    position: { lat: 38.7103, lng: -9.1448 },
    sector: "food",
    area: "restaurant",
    parish: "Misericórdia",
    rating: 4.5,
    forSale: true,
    saleType: "business",
    price: 120000
  },
  {
    id: 3,
    name: "Loja de Roupas Chiado",
    position: { lat: 38.7113, lng: -9.1408 },
    sector: "retail",
    area: "clothing",
    parish: "Santa Maria Maior",
    rating: 4.0,
    forSale: false
  },
  {
    id: 4,
    name: "Padaria Tradicional",
    position: { lat: 38.7153, lng: -9.1428 },
    sector: "food",
    area: "bakery",
    parish: "Misericórdia",
    rating: 4.7,
    forSale: true,
    saleType: "property",
    price: 350000
  },
  {
    id: 5,
    name: "Mercearia do Bairro",
    position: { lat: 38.7193, lng: -9.1398 },
    sector: "retail",
    area: "grocery",
    parish: "Santa Maria Maior",
    rating: 4.3,
    forSale: false
  }
];

const BusinessMap = () => {
  const [businesses, setBusinesses] = useState(mockBusinesses);
  const [filteredBusinesses, setFilteredBusinesses] = useState(mockBusinesses);
  const [selectedBusiness, setSelectedBusiness] = useState(null);
  const [selectedSector, setSelectedSector] = useState("");
  const [selectedArea, setSelectedArea] = useState("");
  const [selectedParish, setSelectedParish] = useState("");
  const [radius, setRadius] = useState(2);
  const [showOnlyForSale, setShowOnlyForSale] = useState(false);
  const [averageRatings, setAverageRatings] = useState({});

  // Filtrar estabelecimentos
  useEffect(() => {
    let filtered = businesses;

    if (selectedSector) {
      filtered = filtered.filter(business => business.sector === selectedSector);
    }

    if (selectedArea) {
      filtered = filtered.filter(business => business.area === selectedArea);
    }

    if (selectedParish) {
      filtered = filtered.filter(business => business.parish === selectedParish);
    }

    if (showOnlyForSale) {
      filtered = filtered.filter(business => business.forSale);
    }

    setFilteredBusinesses(filtered);
  }, [businesses, selectedSector, selectedArea, selectedParish, radius, showOnlyForSale]);

  // Calcular médias
  useEffect(() => {
    const calculateAverages = () => {
      const sectorRatings = {};
      const areaRatings = {};

      businesses.forEach(business => {
        if (business.sector) {
          if (!sectorRatings[business.sector]) {
            sectorRatings[business.sector] = { sum: 0, count: 0 };
          }
          sectorRatings[business.sector].sum += business.rating;
          sectorRatings[business.sector].count += 1;
        }

        if (business.area) {
          if (!areaRatings[business.area]) {
            areaRatings[business.area] = { sum: 0, count: 0 };
          }
          areaRatings[business.area].sum += business.rating;
          areaRatings[business.area].count += 1;
        }
      });

      const averages = {};

      Object.keys(sectorRatings).forEach(sector => {
        const { sum, count } = sectorRatings[sector];
        averages[`sector_${sector}`] = (sum / count).toFixed(1);
      });

      Object.keys(areaRatings).forEach(area => {
        const { sum, count } = areaRatings[area];
        averages[`area_${area}`] = (sum / count).toFixed(1);
      });

      setAverageRatings(averages);
    };

    calculateAverages();
  }, [businesses]);

  // Funções auxiliares para obter nomes
  const getSectorName = (sectorId) => {
    const sector = businessSectors.find(s => s.id === sectorId);
    return sector ? sector.namePortuguese : sectorId;
  };

  const getAreaName = (areaId) => {
    const area = businessAreas.find(a => a.id === areaId);
    return area ? area.namePortuguese : areaId;
  };

  return (
    <PageContainer>
      <FilterContainer>
        <FilterTitle>Filtros</FilterTitle>

        <FilterGroup>
          <FilterLabel>Setor</FilterLabel>
          <Select 
            value={selectedSector}
            onChange={(e) => setSelectedSector(e.target.value)}
          >
            <option value="">Todos os Setores</option>
            {businessSectors.map(sector => (
              <option key={sector.id} value={sector.id}>
                {sector.namePortuguese}
              </option>
            ))}
          </Select>
        </FilterGroup>

        <FilterGroup>
          <FilterLabel>Área de Negócio</FilterLabel>
          <Select 
            value={selectedArea}
            onChange={(e) => setSelectedArea(e.target.value)}
            disabled={!selectedSector}
          >
            <option value="">Todas as Áreas</option>
            {businessAreas
              .filter(area => !selectedSector || area.sectorId === selectedSector)
              .map(area => (
                <option key={area.id} value={area.id}>
                  {area.namePortuguese}
                </option>
              ))}
          </Select>
        </FilterGroup>

        <FilterGroup>
          <FilterLabel>Freguesia</FilterLabel>
          <Select 
            value={selectedParish}
            onChange={(e) => setSelectedParish(e.target.value)}
          >
            <option value="">Todas as Freguesias</option>
            {lisboaParishes.map(parish => (
              <option key={parish} value={parish}>
                {parish}
              </option>
            ))}
          </Select>
        </FilterGroup>

        <FilterGroup>
          <FilterLabel>Apenas à Venda</FilterLabel>
          <Checkbox 
            type="checkbox"
            checked={showOnlyForSale}
            onChange={(e) => setShowOnlyForSale(e.target.checked)}
          />
        </FilterGroup>

        <StatsContainer>
          <StatsTitle>Médias de Desempenho</StatsTitle>

          {selectedSector && (
            <StatItem>
              <StatLabel>Média do Setor:</StatLabel>
              <StatValue>{averageRatings[`sector_${selectedSector}`] || 'N/A'}</StatValue>
            </StatItem>
          )}

          {selectedArea && (
            <StatItem>
              <StatLabel>Média da Área:</StatLabel>
              <StatValue>{averageRatings[`area_${selectedArea}`] || 'N/A'}</StatValue>
            </StatItem>
          )}
        </StatsContainer>

        <LegendContainer>
          <LegendTitle>Legenda</LegendTitle>
          <LegendItem>
            <LegendIcon forSale={false} />
            <LegendText>Estabelecimento Normal</LegendText>
          </LegendItem>
          <LegendItem>
            <LegendIcon forSale={true} />
            <LegendText>Estabelecimento à Venda</LegendText>
          </LegendItem>
        </LegendContainer>
      </FilterContainer>

      <ContentContainer>
        <MapWrapper>
          <MapContainer 
            center={[38.7223, -9.1393]} 
            zoom={13} 
            style={{ height: '100%', width: '100%' }}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />

            {filteredBusinesses.map(business => (
              <CircleMarker
                key={business.id}
                center={[business.position.lat, business.position.lng]}
                radius={8}
                pathOptions={{
                  fillColor: business.forSale ? '#EA4335' : '#4285F4',
                  color: '#FFFFFF',
                  weight: 1,
                  fillOpacity: 0.8
                }}
                eventHandlers={{
                  click: () => setSelectedBusiness(business)
                }}
              >
                <Popup>
                  <BusinessPopup>
                    <BusinessName>{business.name}</BusinessName>
                    {business.forSale && (
                      <ForSaleTag>À Venda</ForSaleTag>
                    )}
                    <BusinessInfo>
                      <InfoItem>
                        <InfoLabel>Setor:</InfoLabel>
                        <InfoValue>{getSectorName(business.sector)}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Área:</InfoLabel>
                        <InfoValue>{getAreaName(business.area)}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Freguesia:</InfoLabel>
                        <InfoValue>{business.parish}</InfoValue>
                      </InfoItem>
                      <InfoItem>
                        <InfoLabel>Avaliação:</InfoLabel>
                        <InfoValue>{business.rating}</InfoValue>
                      </InfoItem>
                      {business.forSale && (
                        <>
                          <InfoItem>
                            <InfoLabel>Tipo:</InfoLabel>
                            <InfoValue>
                              {business.saleType === 'business' ? 'Negócio' : 'Imóvel'}
                            </InfoValue>
                          </InfoItem>
                          <InfoItem>
                            <InfoLabel>Preço:</InfoLabel>
                            <InfoValue>{business.price.toLocaleString()}€</InfoValue>
                          </InfoItem>
                        </>
                      )}
                    </BusinessInfo>
                  </BusinessPopup>
                </Popup>
              </CircleMarker>
            ))}
          </MapContainer>
        </MapWrapper>
      </ContentContainer>
    </PageContainer>
  );
};

// Estilos
const PageContainer = styled.div`
  display: flex;
  height: calc(100vh - 80px);
  background-color: #f5f7fa;
`;

const FilterContainer = styled.div`
  width: 280px;
  padding: 20px;
  background-color: white;
  box-shadow: 2px 0 10px rgba(0, 0, 0, 0.05);
  overflow-y: auto;
`;

const FilterTitle = styled.h2`
  font-size: 20px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 20px;
`;

const FilterGroup = styled.div`
  margin-bottom: 15px;
`;

const FilterLabel = styled.label`
  display: block;
  font-size: 14px;
  font-weight: 500;
  color: #7f8c8d;
  margin-bottom: 5px;
`;

const Select = styled.select`
  width: 100%;
  padding: 8px 10px;
  border: 1px solid #dcdfe6;
  border-radius: 4px;
  font-size: 14px;
  color: #2c3e50;
  background-color: white;

  &:disabled {
    background-color: #f5f7fa;
    cursor: not-allowed;
  }
`;

const Checkbox = styled.input`
  width: 18px;
  height: 18px;
  accent-color: #3498db;
`;

const ContentContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  padding: 20px;
`;

const MapWrapper = styled.div`
  flex: 1;
  border-radius: 8px;
  overflow: hidden;
  box-shadow: 0 2px 12px rgba(0, 0, 0, 0.1);
`;

const StatsContainer = styled.div`
  margin-top: 20px;
  padding: 15px;
  background-color: #f8fafc;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
`;

const StatsTitle = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 10px;
`;

const StatItem = styled.div`
  display: flex;
  justify-content: space-between;
  margin-bottom: 5px;
`;

const StatLabel = styled.span`
  font-size: 14px;
  color: #7f8c8d;
`;

const StatValue = styled.span`
  font-size: 14px;
  font-weight: 600;
  color: #2c3e50;
`;

const LegendContainer = styled.div`
  margin-top: 15px;
`;

const LegendTitle = styled.h4`
  font-size: 14px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 8px;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 5px;
`;

const LegendIcon = styled.div`
  width: 16px;
  height: 16px;
  border-radius: 50%;
  margin-right: 8px;
  background-color: ${props => props.forSale ? '#EA4335' : '#4285F4'};
`;

const LegendText = styled.span`
  font-size: 13px;
  color: #2c3e50;
`;

const BusinessPopup = styled.div`
  padding: 5px;
  max-width: 250px;
`;

const BusinessName = styled.h3`
  font-size: 16px;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 5px;
`;

const ForSaleTag = styled.div`
  display: inline-block;
  background-color: #EA4335;
  color: white;
  font-size: 12px;
  font-weight: 500;
  padding: 3px 8px;
  border-radius: 4px;
  margin-bottom: 10px;
`;

const BusinessInfo = styled.div`
  margin-bottom: 10px;
`;

const InfoItem = styled.div`
  display: flex;
  margin-bottom: 5px;
`;

const InfoLabel = styled.span`
  font-size: 13px;
  font-weight: 500;
  color: #7f8c8d;
  width: 80px;
`;

const InfoValue = styled.span`
  font-size: 13px;
  color: #2c3e50;
`;

export default BusinessMap;
